            if (!num1Input || !num2Input) {
                throw new Error('ä¸¡æ–¹ã®æ•°å€¤ã‚’å…¥åŠ›ã—ã¦ãã ã•ã„');
            }

            // æ•°å€¤ã«å¤‰æ›
            const num1 = parseFloat(num1Input);
