    // === ç‰¹æ®Šè¨ˆç®—ã®å®Ÿè£… ===

    // å¹³æ–¹æ ¹è¨ˆç®—
    document.getElementById('sqrt-btn').addEventListener('click', function() {
        const input = document.getElementById('sqrt-input').value;
