/* Bootstrapç‰ˆ ç°¡å˜è¨ˆç®—æ©Ÿ ã‚«ã‚¹ã‚¿ãƒ ã‚¹ã‚¿ã‚¤ãƒ« */

/* ãƒ™ãƒ¼ã‚¹ã‚¹ã‚¿ã‚¤ãƒ« */
body {
    font-family: 'Arial', sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: #333;
}

/* ã‚¿ã‚¤ãƒˆãƒ«ã‚¹ã‚¿ã‚¤ãƒ« */
h1 {
    font-size: 2.5rem;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

/* ã‚«ãƒ¼ãƒ‰ã®ãƒ›ãƒãƒ¼ã‚¨ãƒ•ã‚§ã‚¯ãƒˆ */
.card {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    box-shadow: 0 8px 32px rgba(0,0,0,0.1);
}

.card:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 40px rgba(0,0,0,0.15);
}

/* ã‚«ãƒ¼ãƒ‰ã‚¿ã‚¤ãƒˆãƒ« */
.card-title {
    color: #4a5568;
    font-size: 1.4rem;
    border-bottom: 2px solid #e2e8f0;
    padding-bottom: 10px;
    margin-bottom: 20px;
}

/* ä¸»è¦ãƒœã‚¿ãƒ³ã®ã‚«ã‚¹ã‚¿ãƒã‚¤ã‚º */
.btn-primary {
    background: linear-gradient(45deg, #667eea, #764ba2);
    border: none;
    box-shadow: 0 4px 15px rgba(102, 126, 234, 0.3);
    transition: all 0.3s ease;
    font-weight: 600;
}

.btn-primary:hover {
    background: linear-gradient(45deg, #5a67d8, #6b46c1);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(102, 126, 234, 0.4);
}

.btn-primary:active {
    transform: translateY(0);
}

/* æˆåŠŸãƒœã‚¿ãƒ³ã®ã‚«ã‚¹ã‚¿ãƒã‚¤ã‚º */
.btn-success {
    background: linear-gradient(45deg, #48bb78, #38a169);
    border: none;
    transition: all 0.3s ease;
    font-weight: 500;
}

.btn-success:hover {
    background: linear-gradient(45deg, #38a169, #2f855a);
    transform: translateY(-1px);
}

/* å‰Šé™¤ãƒœã‚¿ãƒ³ã®ã‚«ã‚¹ã‚¿ãƒã‚¤ã‚º */
.btn-danger {
    background: linear-gradient(45deg, #f56565, #e53e3e);
    border: none;
    transition: all 0.3s ease;
}

.btn-danger:hover {
    background: linear-gradient(45deg, #e53e3e, #c53030);
    transform: translateY(-1px);
}

/* ãƒ•ã‚©ãƒ¼ãƒ ã‚³ãƒ³ãƒˆãƒ­ãƒ¼ãƒ«ã®ãƒ•ã‚©ãƒ¼ã‚«ã‚¹ */
.form-control:focus,
.form-select:focus {
    border-color: #667eea;
    box-shadow: 0 0 0 0.2rem rgba(102, 126, 234, 0.25);
}

/* çµæœè¡¨ç¤ºã®æˆåŠŸçŠ¶æ…‹ */
.alert-light.success {
    background: #f0fff4;
    border-color: #68d391;
    color: #2d3748;
}

/* è¨ˆç®—çµæœã®è¡¨ç¤ºã‚¹ã‚¿ã‚¤ãƒ« */
.calculation {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    flex-wrap: wrap;
}

.calculation-expression {
    font-weight: 500;
    color: #4a5568;
}

.equals {
    font-weight: bold;
    color: #667eea;
    font-size: 1.4rem;
}

.calculation-result {
    font-weight: bold;
    font-size: 1.4rem;
    color: #2d3748;
}

/* å±¥æ­´ãƒªã‚¹ãƒˆ */
.history-list {
    max-height: 200px;
    overflow-y: auto;
    border: 2px solid #e2e8f0;
    border-radius: 8px;
    padding: 10px;
}

.history-item {
    padding: 8px 12px;
    border-bottom: 1px solid #e2e8f0;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.history-item:last-child {
    border-bottom: none;
}

.history-expression {
    font-weight: 500;
    color: #4a5568;
}

.history-time {
    font-size: 0.8rem;
    color: #a0aec0;
}

/* ç‰¹æ®Šè¨ˆç®—ã®çµæœãƒãƒƒã‚¸ */
.badge.bg-info {
    font-size: 0.9rem;
    padding: 5px 10px;
}

.badge.bg-info.success {
    background-color: #48bb78 !important;
}

.badge.bg-info.error {
    background-color: #f56565 !important;
}

/* ã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã®è¡¨ç¤ºåˆ¶å¾¡ */
.alert.d-none {
    display: none !important;
}

/* å°ç”»é¢ã§ã®èª¿æ•´ */
@media (max-width: 768px) {
    h1 {
        font-size: 2rem;
    }

    .calculation {
        flex-direction: column;
        gap: 5px;
    }

    .calculation-result {
        font-size: 1.2rem;
    }
}

@media (max-width: 480px) {
    h1 {
        font-size: 1.8rem;
    }
}

