css.expected:
/* 2つの商品ギャラリーのスタイル */
body {
    font-family: 'Arial', sans-serif;
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    min-height: 100vh;
}

.container {
    max-width: 1200px;
}

h2 {
    color: #2c3e50;
    font-weight: bold;
    margin-bottom: 1rem;
}

.card {
    border: none;
    border-radius: 15px;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
    overflow: hidden;
}

.card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
}

.card-img-top {
    height: 250px;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.card:hover .card-img-top {
    transform: scale(1.05);
}

.card-body {
    padding: 2rem;
}

.card-title {
    color: #2c3e50;
    font-weight: bold;
    font-size: 1.25rem;
    margin-bottom: 1rem;
}

.card-text {
    color: #7f8c8d;
    line-height: 1.6;
    margin-bottom: 1.5rem;
}

.btn {
    border-radius: 25px;
    padding: 0.5rem 1.5rem;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    transition: all 0.3s ease;
}

.btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.text-primary {
    color: #3498db !important;
}

.text-success {
    color: #2ecc71 !important;
}

@media (max-width: 768px) {
    .container {
        margin-top: 20px;
        padding: 15px;
    }

    .card-body {
        padding: 1.5rem;
    }

    .card-img-top {
        height: 200px;
    }

    h2 {
        font-size: 1.5rem;
    }
}

/* カスタムアニメーション */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.card {
    animation: fadeInUp 0.6s ease forwards;
}

.card:nth-child(2) {
    animation-delay: 0.2s;
}

