// 2つの商品ギャラリーのJavaScript

document.addEventListener('DOMContentLoaded', function() {
    console.log('2つの商品ギャラリーが読み込まれました');

    // 商品データ
    const products = [
        {
            name: 'プレミアム Tシャツ',
            price: 2980,
            description: '高品質なコットン100%で作られた、着心地抜群のプレミアムTシャツです。',
            image: 'https://via.placeholder.com/400x250/ff6b6b/white?text=商品1'
        },
        {
            name: 'カジュアル パンツ',
            price: 4200,
            description: '動きやすさを重視したカジュアルパンツです。オフィスでもプライベートでも活躍します。',
            image: 'https://via.placeholder.com/400x250/4ecdc4/white?text=商品2'
        }
    ];

    // カードのホバーエフェクト
    const cards = document.querySelectorAll('.card');
    cards.forEach(card => {
        card.addEventListener('mouseenter', function() {
            console.log('カードにマウスオーバーしました');
        });

        card.addEventListener('mouseleave', function() {
            console.log('カードからマウスアウトしました');
        });
    });

    // ボタンクリック処理
    const buttons = document.querySelectorAll('.btn');
    buttons.forEach((button, index) => {
        button.addEventListener('click', function(e) {
            e.preventDefault();
            const productName = products[index] ? products[index].name : `商品${index + 1}`;
            alert(`${productName}の詳細ページに移動します！\n\n実際のECサイトでは商品詳細ページが表示されます。`);
            console.log(`${productName}の詳細ボタンがクリックされました`);
        });
    });

    // レスポンシブ対応の確認
    function checkResponsive() {
        const width = window.innerWidth;
        if (width < 768) {
            console.log('モバイルビューです');
        } else {
            console.log('デスクトップビューです');
        }
    }

    // 初期チェック
    checkResponsive();

    // ウィンドウサイズ変更時のチェック
    window.addEventListener('resize', checkResponsive);

    // スムーズスクロール効果（必要に応じて）
    const smoothScroll = (target) => {
        document.querySelector(target).scrollIntoView({
            behavior: 'smooth'
        });
    };

    // 商品データをコンソールに出力（デバッグ用）
    console.log('商品データ:', products);
    console.log('カード要素数:', cards.length);
    console.log('ボタン要素数:', buttons.length);
});

// パフォーマンス測定
window.addEventListener('load', function() {
    const loadTime = performance.now();
    console.log(`ページの読み込み完了: ${Math.round(loadTime)}ms`);
});
