// Bootstrap商品ギャラリーのJavaScript

document.addEventListener('DOMContentLoaded', function() {
    console.log('Bootstrap商品ギャラリーが読み込まれました');

    // 商品データ
    const products = {
        tshirt: {
            name: 'プレミアム Tシャツ',
            price: 2980,
            originalPrice: 3980,
            description: '高品質なコットン100%で作られた、着心地抜群のプレミアムTシャツです。',
            features: ['コットン100%', '洗濯機対応', 'シワになりにくい', '快適な着心地']
        },
        pants: {
            name: 'カジュアル パンツ',
            price: 4200,
            description: '動きやすさを重視したカジュアルパンツです。オフィスでもプライベートでも活躍します。',
            features: ['ストレッチ素材', '多彩なポケット', 'シワになりにくい', 'オールシーズン対応']
        }
    };

    // カート配列
    let cart = [];

    console.log('商品データ:', products);
});

// 商品詳細を表示
function showProductDetails(productId) {
    const products = {
        tshirt: {
            name: 'プレミアム Tシャツ',
            price: 2980,
            features: ['コットン100%', '洗濯機対応', 'シワになりにくい', '快適な着心地']
        },
        pants: {
            name: 'カジュアル パンツ',
            price: 4200,
            features: ['ストレッチ素材', '多彩なポケット', 'シワになりにくい', 'オールシーズン対応']
        }
    };

    const product = products[productId];
    if (product) {
        const features = product.features.join('\n• ');
        alert(`${product.name}\n\n価格: ¥${product.price.toLocaleString()}\n\n特徴:\n• ${features}\n\n実際のECサイトでは詳細ページに移動します！`);
        console.log(`${product.name}の詳細を表示しました`);
    }
}

// カートに追加
function addToCart(productId) {
    const products = {
        tshirt: { name: 'プレミアム Tシャツ', price: 2980 },
        pants: { name: 'カジュアル パンツ', price: 4200 }
    };

    const product = products[productId];
    if (product) {
        // カートに商品を追加（実際の実装では配列に保存）
        showSuccessAlert(`${product.name}をカートに追加しました！`);
        console.log(`${product.name}をカートに追加:`, product);

        // ボタンに一時的な効果を追加
        const button = event.target;
        const originalText = button.textContent;
        button.textContent = '追加完了！';
        button.disabled = true;

        setTimeout(() => {
            button.textContent = originalText;
            button.disabled = false;
        }, 2000);
    }
}

// お気に入りトグル
function toggleFavorite(button) {
    const isFavorited = button.classList.contains('favorited');

    if (isFavorited) {
        button.classList.remove('favorited');
        button.innerHTML = '<i class="bi bi-heart"></i> ♡';
        console.log('お気に入りから削除しました');
    } else {
        button.classList.add('favorited');
        button.innerHTML = '<i class="bi bi-heart-fill"></i> ♥';
        showSuccessAlert('お気に入りに追加しました！');
        console.log('お気に入りに追加しました');
    }
}

// 成功アラートを表示
function showSuccessAlert(message) {
    const alert = document.getElementById('successAlert');
    const messageSpan = document.getElementById('successMessage');

    messageSpan.textContent = message;
    alert.style.display = 'block';
    alert.classList.add('show');

    // 3秒後に自動で非表示
    setTimeout(() => {
        hideAlert('successAlert');
    }, 3000);
}

// アラートを非表示
function hideAlert(alertId) {
    const alert = document.getElementById(alertId);
    alert.classList.remove('show');
    setTimeout(() => {
        alert.style.display = 'none';
    }, 300);
}

// カードホバー効果の拡張
document.addEventListener('DOMContentLoaded', function() {
    const cards = document.querySelectorAll('.card');

    cards.forEach(card => {
        card.addEventListener('mouseenter', function() {
            this.style.zIndex = '10';
            console.log('カードにマウスオーバー');
        });

        card.addEventListener('mouseleave', function() {
            this.style.zIndex = '1';
            console.log('カードからマウスアウト');
        });
    });

    // Bootstrap JavaScript の動作確認
    console.log('Bootstrap JavaScript が利用可能:', typeof bootstrap !== 'undefined');
});

// パフォーマンス監視
window.addEventListener('load', function() {
    const loadTime = performance.now();
    console.log(`ページ読み込み完了時間: ${Math.round(loadTime)}ms`);

    // 使用している Bootstrap コンポーネントをログに記録
    const components = ['Alert', 'Button', 'Card', 'Grid'];
    console.log('使用中のBootstrapコンポーネント:', components);
});
