js.expected:
// Bootstrap基礎システムのJavaScript

// グローバル変数
let cart = [];
let cartTotal = 0;

// 商品データ
const products = {
    'smart-tshirt': {
        name: 'スマートTシャツ',
        price: 3980,
        image: 'https://via.placeholder.com/100x100/007bff/white?text=ST'
    },
    'premium-pants': {
        name: 'プレミアムパンツ',
        price: 5200,
        image: 'https://via.placeholder.com/100x100/28a745/white?text=PP'
    },
    'stylish-shoes': {
        name: 'スタイリッシュシューズ',
        price: 8800,
        image: 'https://via.placeholder.com/100x100/dc3545/white?text=SS'
    }
};

// DOMが読み込まれた後の初期化
document.addEventListener('DOMContentLoaded', function() {
    console.log('Bootstrap基礎システムが初期化されました');

    // Bootstrap コンポーネントの初期化
    initializeBootstrapComponents();

    // ナビゲーションの初期化
    initializeNavigation();

    // イベントリスナーの設定
    setupEventListeners();

    // カルーセル自動再生の設定
    setupCarousel();

    console.log('すべてのコンポーネントが初期化されました');
});

// Bootstrap コンポーネントの初期化
function initializeBootstrapComponents() {
    // カルーセルの初期化
    const carousel = document.querySelector('#heroCarousel');
    if (carousel) {
        new bootstrap.Carousel(carousel, {
            interval: 5000,
            wrap: true
        });
        console.log('カルーセルを初期化しました');
    }

    // ツールチップの初期化
    const tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'));
    tooltipTriggerList.map(function (tooltipTriggerEl) {
        return new bootstrap.Tooltip(tooltipTriggerEl);
    });

    console.log('Bootstrapコンポーネントの初期化完了');
}

// ナビゲーションの初期化
function initializeNavigation() {
    const navLinks = document.querySelectorAll('.nav-link');

    navLinks.forEach(link => {
        link.addEventListener('click', function(e) {
            if (this.getAttribute('href').startsWith('#')) {
                e.preventDefault();
                const targetId = this.getAttribute('href');
                const targetElement = document.querySelector(targetId);

                if (targetElement) {
                    // アクティブクラスの更新
                    navLinks.forEach(nl => nl.classList.remove('active'));
                    this.classList.add('active');

                    // スムーズスクロール
                    targetElement.scrollIntoView({
                        behavior: 'smooth',
                        block: 'start'
                    });

                    console.log(`${targetId} セクションにナビゲートしました`);
                }
            }
        });
    });
}

// イベントリスナーの設定
function setupEventListeners() {
    // モーダルイベント
    const loginModal = document.getElementById('loginModal');
    if (loginModal) {
        loginModal.addEventListener('shown.bs.modal', function() {
            console.log('ログインモーダルが表示されました');
            document.getElementById('email').focus();
        });
    }

    const cartModal = document.getElementById('cartModal');
    if (cartModal) {
        cartModal.addEventListener('shown.bs.modal', function() {
            console.log('カートモーダルが表示されました');
            updateCartDisplay();
        });
    }

    const contactModal = document.getElementById('contactModal');
    if (contactModal) {
        contactModal.addEventListener('shown.bs.modal', function() {
            console.log('お問い合わせモーダルが表示されました');
            document.getElementById('contactName').focus();
        });
    }
}

// カルーセルの設定
function setupCarousel() {
    const carousel = document.querySelector('#heroCarousel');
    if (carousel) {
        carousel.addEventListener('slide.bs.carousel', function(event) {
            console.log(`カルーセルスライド ${event.to + 1} に切り替えました`);
        });
    }
}

// カートに商品を追加
function addToCart(productId) {
    const product = products[productId];
    if (!product) {
        console.error('商品が見つかりません:', productId);
        return;
    }

    // 既存商品の確認
    const existingItem = cart.find(item => item.id === productId);

    if (existingItem) {
        existingItem.quantity += 1;
        console.log(`${product.name}の数量を増やしました (${existingItem.quantity})`);
    } else {
        cart.push({
            id: productId,
            name: product.name,
            price: product.price,
            image: product.image,
            quantity: 1
        });
        console.log(`${product.name}をカートに追加しました`);
    }

    updateCartCount();
    showAddToCartFeedback(event.target, product.name);
}

// カート数を更新
function updateCartCount() {
    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
    const cartCountElement = document.getElementById('cartCount');

    if (cartCountElement) {
        cartCountElement.textContent = totalItems;

        // アニメーション効果
        cartCountElement.style.transform = 'scale(1.5)';
        setTimeout(() => {
            cartCountElement.style.transform = 'scale(1)';
        }, 300);
    }

    console.log(`カート商品数: ${totalItems}`);
}

// カート表示を更新
function updateCartDisplay() {
    const cartItemsContainer = document.getElementById('cartItems');
    const cartTotalElement = document.getElementById('cartTotal');

    if (cart.length === 0) {
        cartItemsContainer.innerHTML = '<p class="text-muted text-center">カートは空です</p>';
        cartTotalElement.textContent = '¥0';
        return;
    }

    let cartHTML = '';
    let total = 0;

    cart.forEach(item => {
        const itemTotal = item.price * item.quantity;
        total += itemTotal;

        cartHTML += `
            <div class="d-flex align-items-center mb-3 p-2 border-bottom">
                <img src="${item.image}" alt="${item.name}" class="me-3" style="width: 60px; height: 60px; object-fit: cover; border-radius: 8px;">
                <div class="flex-grow-1">
                    <h6 class="mb-0">${item.name}</h6>
                    <small class="text-muted">¥${item.price.toLocaleString()}</small>
                </div>
                <div class="d-flex align-items-center">
                    <button class="btn btn-outline-secondary btn-sm me-2" onclick="updateCartQuantity('${item.id}', ${item.quantity - 1})">
                        <i class="bi bi-dash"></i>
                    </button>
                    <span class="mx-2">${item.quantity}</span>
                    <button class="btn btn-outline-secondary btn-sm ms-2" onclick="updateCartQuantity('${item.id}', ${item.quantity + 1})">
                        <i class="bi bi-plus"></i>
                    </button>
                </div>
            </div>
        `;
    });

    cartItemsContainer.innerHTML = cartHTML;
    cartTotalElement.textContent = `¥${total.toLocaleString()}`;

    console.log('カート表示を更新しました');
}

// カート数量を更新
function updateCartQuantity(productId, newQuantity) {
    if (newQuantity <= 0) {
        cart = cart.filter(item => item.id !== productId);
        console.log(`${productId}をカートから削除しました`);
    } else {
        const item = cart.find(item => item.id === productId);
        if (item) {
            item.quantity = newQuantity;
            console.log(`${item.name}の数量を${newQuantity}に変更しました`);
        }
    }

    updateCartCount();
    updateCartDisplay();
}

// カート追加フィードバック
function showAddToCartFeedback(button, productName) {
    const originalText = button.textContent;
    const originalClass = button.className;

    button.textContent = '追加完了!';
    button.className = button.className.replace('btn-primary', 'btn-success').replace('btn-success', 'btn-success').replace('btn-danger', 'btn-success');
    button.disabled = true;

    setTimeout(() => {
        button.textContent = originalText;
        button.className = originalClass;
        button.disabled = false;
    }, 1500);

    // トースト通知（簡易版）
    showToast(`${productName}をカートに追加しました`);
}

// トースト通知
function showToast(message) {
    // 簡易的なトースト実装
    const toast = document.createElement('div');
    toast.className = 'position-fixed bottom-0 end-0 p-3';
    toast.style.zIndex = '9999';
    toast.innerHTML = `
        <div class="toast show" role="alert">
            <div class="toast-header">
                <i class="bi bi-check-circle-fill text-success me-2"></i>
                <strong class="me-auto">通知</strong>
                <button type="button" class="btn-close" data-bs-dismiss="toast"></button>
            </div>
            <div class="toast-body">
                ${message}
            </div>
        </div>
    `;

    document.body.appendChild(toast);

    setTimeout(() => {
        toast.remove();
    }, 3000);
}

// パフォーマンス監視
window.addEventListener('load', function() {
    const loadTime = performance.now();
    console.log(`Bootstrap基礎システムの読み込み完了: ${Math.round(loadTime)}ms`);

    // 使用中のBootstrapコンポーネントをログ出力
    const components = {
        'Navbar': document.querySelector('.navbar') !== null,
        'Carousel': document.querySelector('.carousel') !== null,
        'Modal': document.querySelectorAll('.modal').length,
        'Cards': document.querySelectorAll('.card').length,
        'Buttons': document.querySelectorAll('.btn').length,
        'Alerts': document.querySelectorAll('.alert').length
    };

    console.log('使用中のBootstrapコンポーネント:', components);
    console.log('カート配列:', cart);
    console.log('商品データ:', products);
});

// スクロール時のナビゲーション更新
window.addEventListener('scroll', function() {
    const sections = document.querySelectorAll('section[id]');
    const navLinks = document.querySelectorAll('.nav-link');

    let currentSection = '';

    sections.forEach(section => {
        const sectionTop = section.offsetTop - 100;
        const sectionHeight = section.offsetHeight;
        const scrollPosition = window.pageYOffset;

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
            currentSection = section.getAttribute('id');
        }
    });

    navLinks.forEach(link => {
        link.classList.remove('active');
        if (link.getAttribute('href') === '#' + currentSection) {
            link.classList.add('active');
        }
    });
});
