/* Bootstrap総合システム - プロフェッショナルスタイル */

/* カスタムプロパティ（CSS Variables） */
:root {
    --bs-primary-rgb: 13, 110, 253;
    --gradient-primary: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    --gradient-success: linear-gradient(135deg, #11998e 0%, #38ef7d 100%);
    --gradient-danger: linear-gradient(135deg, #ff416c 0%, #ff4b2b 100%);
    --gradient-warning: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    --shadow-lg: 0 1rem 3rem rgba(0, 0, 0, 0.175);
    --transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* 基本設定 */
body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    scroll-behavior: smooth;
}

/* ユーティリティクラス */
.min-vh-50 {
    min-height: 50vh;
}

/* ナビゲーション */
.navbar {
    backdrop-filter: blur(10px);
    box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.05);
    transition: var(--transition);
}

.navbar.scrolled {
    background-color: rgba(255, 255, 255, 0.95) !important;
}

.navbar-brand {
    font-weight: 800;
    letter-spacing: -0.5px;
}

.nav-link {
    font-weight: 500;
    transition: var(--transition);
    position: relative;
}

.nav-link::after {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: -5px;
    left: 50%;
    background: var(--gradient-primary);
    transition: var(--transition);
    transform: translateX(-50%);
}

.nav-link:hover::after {
    width: 100%;
}

/* ヒーローセクション */
.hero-slide {
    background: var(--gradient-primary);
    min-height: 60vh;
    display: flex;
    align-items: center;
    position: relative;
    overflow: hidden;
}

.hero-slide::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"><g fill="none" fill-rule="evenodd"><g fill="%23ffffff" fill-opacity="0.05"><circle cx="36" cy="24" r="5"/><circle cx="6" cy="44" r="5"/><circle cx="36" cy="8" r="5"/></g></g></svg>');
}

.hero-slide.bg-primary {
    background: var(--gradient-primary);
}

.hero-slide.bg-success {
    background: var(--gradient-success);
}

.hero-slide.bg-danger {
    background: var(--gradient-danger);
}

.carousel-control-prev-icon,
.carousel-control-next-icon {
    background-size: 20px 20px;
    border-radius: 50%;
    background-color: rgba(255, 255, 255, 0.3);
    width: 3rem;
    height: 3rem;
}

/* 統計セクション */
.stat-item {
    transition: var(--transition);
    padding: 2rem;
    border-radius: 15px;
    background: white;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
}

.stat-item:hover {
    transform: translateY(-10px);
    box-shadow: var(--shadow-lg);
}

.counter {
    font-weight: 800;
    color: #2c3e50;
}

/* カード */
.card {
    border: none;
    border-radius: 20px;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
    transition: var(--transition);
    overflow: hidden;
    position: relative;
}

.card::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    transition: var(--transition);
}

.card:hover::before {
    left: 100%;
}

.card:hover {
    transform: translateY(-15px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
}

.card-img-top {
    height: 250px;
    object-fit: cover;
    transition: var(--transition);
}

.card:hover .card-img-top {
    transform: scale(1.1);
}

.product-card {
    position: relative;
}

.product-badge {
    position: absolute;
    top: 1rem;
    left: 1rem;
    z-index: 10;
    border-radius: 25px;
    padding: 0.5rem 1rem;
    font-size: 0.8rem;
    font-weight: 600;
}

.product-actions {
    position: absolute;
    top: 1rem;
    right: 1rem;
    z-index: 10;
    opacity: 0;
    transition: var(--transition);
}

.card:hover .product-actions {
    opacity: 1;
}

.product-rating {
    color: #ffc107;
}

/* ボタン */
.btn {
    border-radius: 25px;
    padding: 0.75rem 2rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    transition: var(--transition);
    position: relative;
    overflow: hidden;
}

.btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: var(--transition);
}

.btn:hover::before {
    left: 100%;
}

.btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
}

.btn-primary {
    background: var(--gradient-primary);
    border: none;
}

.btn-success {
    background: var(--gradient-success);
    border: none;
}

.btn-danger {
    background: var(--gradient-danger);
    border: none;
}

.btn-warning {
    background: var(--gradient-warning);
    border: none;
}

/* フィルターボタン */
.btn-check:checked + .btn-outline-primary {
    background: var(--gradient-primary);
    border-color: transparent;
}

/* オフキャンバス */
.offcanvas {
    border-radius: 20px 0 0 20px;
    box-shadow: var(--shadow-lg);
}

.offcanvas-header {
    background: var(--gradient-primary);
}

.offcanvas-footer {
    background: #f8f9fa;
}

/* モーダル */
.modal-content {
    border: none;
    border-radius: 20px;
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.25);
    overflow: hidden;
}

.modal-header {
    background: var(--gradient-primary);
    border-bottom: none;
}

.modal-body {
    padding: 2rem;
}

/* フォーム */
.form-control {
    border-radius: 15px;
    border: 2px solid #e9ecef;
    padding: 0.875rem 1.125rem;
    transition: var(--transition);
}

.form-control:focus {
    border-color: #667eea;
    box-shadow: 0 0 0 0.25rem rgba(102, 126, 234, 0.25);
}

.form-select {
    border-radius: 15px;
    border: 2px solid #e9ecef;
    transition: var(--transition);
}

.input-group-text {
    border-radius: 15px 0 0 15px;
    background: #f8f9fa;
    border: 2px solid #e9ecef;
    border-right: none;
}

.input-group .form-control {
    border-radius: 0 15px 15px 0;
    border-left: none;
}

.input-group .btn {
    border-radius: 0 15px 15px 0;
}

/* ページネーション */
.pagination .page-link {
    border-radius: 15px;
    margin: 0 0.25rem;
    border: none;
    padding: 0.75rem 1rem;
    transition: var(--transition);
}

.pagination .page-item.active .page-link {
    background: var(--gradient-primary);
    box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
}

/* フッター */
footer {
    background: linear-gradient(135deg, #2c3e50 0%, #34495e 100%);
    position: relative;
}

footer::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg"><g fill="none" fill-rule="evenodd"><g fill="%23ffffff" fill-opacity="0.03"><polygon points="0 40 40 0 40 40"/></g></g></svg>');
}

.social-links a {
    transition: var(--transition);
    display: inline-block;
}

.social-links a:hover {
    transform: translateY(-3px);
    color: #667eea !important;
}

/* Back to Top ボタン */
#backToTop {
    z-index: 1000;
    transition: var(--transition);
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
}

#backToTop:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
}

/* トースト */
.toast {
    border-radius: 15px;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
}

/* アニメーション */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(50px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideInLeft {
    from {
        opacity: 0;
        transform: translateX(-50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideInRight {
    from {
        opacity: 0;
        transform: translateX(50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes countUp {
    from {
        opacity: 0;
        transform: scale(0.5);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

/* スクロール時のアニメーション */
.animate-on-scroll {
    opacity: 0;
    transition: var(--transition);
}

.animate-on-scroll.animated {
    opacity: 1;
    animation: fadeInUp 0.8s ease forwards;
}

/* レスポンシブ対応 */
@media (max-width: 1200px) {
    .hero-slide {
        min-height: 50vh;
    }
}

@media (max-width: 992px) {
    .hero-slide {
        min-height: 40vh;
    }

    .card-img-top {
        height: 200px;
    }

    .stat-item {
        padding: 1.5rem;
        margin-bottom: 2rem;
    }
}

@media (max-width: 768px) {
    .hero-slide {
        min-height: 60vh;
        text-align: center;
    }

    .btn {
        padding: 0.625rem 1.5rem;
        font-size: 0.9rem;
    }

    .card-img-top {
        height: 180px;
    }

    .navbar-brand {
        font-size: 1.2rem;
    }

    .display-4 {
        font-size: 2.5rem;
    }

    .modal-body {
        padding: 1.5rem;
    }
}

@media (max-width: 576px) {
    .hero-slide {
        min-height: 50vh;
        padding: 2rem 0;
    }

    .display-4 {
        font-size: 2rem;
    }

    .btn {
        padding: 0.5rem 1.25rem;
        font-size: 0.85rem;
        margin-bottom: 0.5rem;
    }

    .card {
        margin-bottom: 1.5rem;
    }

    .stat-item {
        padding: 1rem;
        text-align: center;
    }
}

/* カスタムスクロールバー */
::-webkit-scrollbar {
    width: 10px;
}

::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 5px;
}

::-webkit-scrollbar-thumb {
    background: var(--gradient-primary);
    border-radius: 5px;
}

::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(135deg, #5a67d8, #667eea);
}

/* プリント用スタイル */
@media print {
    .navbar,
    .offcanvas,
    .modal,
    #backToTop,
    .toast-container {
        display: none !important;
    }

    .container {
        max-width: none;
        margin: 0;
        padding: 0;
    }
}

/* 高コントラストモード対応 */
@media (prefers-contrast: high) {
    .card {
        border: 2px solid #000;
    }

    .btn {
        border: 2px solid;
    }
}

/* アニメーション無効化対応 */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }

    .carousel {
        transition: none;
    }
}

/* フォーカススタイルの改善 */
.btn:focus,
.form-control:focus,
.form-select:focus {
    outline: 2px solid #667eea;
    outline-offset: 2px;
}

/* アクセシビリティ向上 */
.visually-hidden-focusable:focus {
    position: static !important;
    width: auto !important;
    height: auto !important;
    margin: 0 !important;
    overflow: visible !important;
    clip: auto !important;
    white-space: normal !important;
}

