// Bootstrap総合システム - プロフェッショナルJavaScript

// グローバル変数とデータ
const APP_CONFIG = {
    version: '1.0.0',
    debug: true,
    apiEndpoint: '/api',
    itemsPerPage: 12
};

let cart = [];
let wishlist = [];
let currentPage = 1;
let currentFilter = 'all';
let currentSort = 'default';

// 商品データベース
const PRODUCTS_DB = [
