        name: 'エルゴノミクス オフィスチェア',
        category: 'home',
        price: 45600,
        rating: 4.9,
        reviews: 67,
        image: 'https://via.placeholder.com/300x250/6f42c1/white?text=Office+Chair',
        badge: 'PREMIUM',
