    },
    {
        id: 5,
        name: 'アクティブ ランニングシューズ',
        category: 'clothing',
        price: 12800,
        rating: 4.3,
        reviews: 194,
        image: 'https://via.placeholder.com/300x250/dc3545/white?text=Running+Shoes',
        badge: 'HOT',
        description: 'プロランナー推奨の高性能シューズ',
        inStock: true
    },
    {
        id: 6,
        name: 'ポータブル Bluetooth スピーカー',
        category: 'electronics',
        price: 8900,
        rating: 4.4,
        reviews: 312,
        image: 'https://via.placeholder.com/300x250/ffc107/black?text=BT+Speaker',
        badge: 'SALE',
        description: '高音質ポータブルBluetoothスピーカー',
        inStock: true
    }
];

// DOM Content Loaded時の初期化
document.addEventListener('DOMContentLoaded', function() {
    console.log('🚀 Bootstrap総合システム v' + APP_CONFIG.version + ' 起動中...');

    // 初期化処理
    initializeApp();

    console.log('✅ システム初期化完了');
});

// アプリケーション初期化
function initializeApp() {
    // Bootstrap コンポーネント初期化
    initializeBootstrapComponents();

    // イベントリスナー設定
    setupEventListeners();

    // UI要素の初期化
    initializeUI();

    // データの読み込み
    loadInitialData();

    // パフォーマンス監視開始
    startPerformanceMonitoring();
}

// Bootstrap コンポーネント初期化
function initializeBootstrapComponents() {
    // カルーセル初期化
    const carousel = document.querySelector('#heroCarousel');
    if (carousel) {
        new bootstrap.Carousel(carousel, {
            interval: 6000,
            wrap: true,
            ride: 'carousel'
        });
        console.log('🎠 カルーセル初期化完了');
    }

    // ツールチップ初期化
    const tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'));
    tooltipTriggerList.map(function (tooltipTriggerEl) {
        return new bootstrap.Tooltip(tooltipTriggerEl);
    });

    // ポップオーバー初期化
    const popoverTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="popover"]'));
    popoverTriggerList.map(function (popoverTriggerEl) {
        return new bootstrap.Popover(popoverTriggerEl);
    });

    console.log('🎨 Bootstrapコンポーネント初期化完了');
}

// イベントリスナー設定
function setupEventListeners() {
    // ナビゲーション
    setupNavigationEvents();

    // 検索機能
    setupSearchEvents();

    // フィルタリング・ソート
    setupFilterSortEvents();

    // モーダルイベント
    setupModalEvents();

    // スクロールイベント
    setupScrollEvents();

    // フォームイベント
    setupFormEvents();

    console.log('📡 イベントリスナー設定完了');
}

// ナビゲーションイベント
function setupNavigationEvents() {
    // スムーズスクロール
    document.querySelectorAll('a[href^="#"]').forEach(link => {
        link.addEventListener('click', function(e) {
            const targetId = this.getAttribute('href');
            if (targetId !== '#' && document.querySelector(targetId)) {
                e.preventDefault();
                scrollToSection(targetId);
            }
        });
    });

    // Back to Top ボタン
    const backToTopBtn = document.getElementById('backToTop');
    if (backToTopBtn) {
        backToTopBtn.addEventListener('click', () => {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        });
    }
}

// 検索機能イベント
function setupSearchEvents() {
    const searchInput = document.getElementById('searchInput');
    if (searchInput) {
        let searchTimeout;
        searchInput.addEventListener('input', function() {
            clearTimeout(searchTimeout);
            searchTimeout = setTimeout(() => {
                performSearch(this.value);
            }, 500);
        });
    }
}

// フィルタリング・ソートイベント
function setupFilterSortEvents() {
    // フィルターボタン
    document.querySelectorAll('input[name="filter"]').forEach(input => {
        input.addEventListener('change', function() {
            if (this.checked) {
                currentFilter = this.id.replace('filter', '').toLowerCase();
                if (currentFilter === 'all') currentFilter = 'all';
                renderProducts();
            }
        });
    });

    // ソートセレクト
    const sortSelect = document.getElementById('sortSelect');
    if (sortSelect) {
        sortSelect.addEventListener('change', function() {
            currentSort = this.value;
            renderProducts();
        });
    }
}

// モーダルイベント
function setupModalEvents() {
    // ログインモーダル
    const loginModal = document.getElementById('loginModal');
    if (loginModal) {
        loginModal.addEventListener('shown.bs.modal', function() {
            document.getElementById('loginEmail')?.focus();
        });

        // パスワード表示切替
        const togglePassword = document.getElementById('togglePassword');
        if (togglePassword) {
            togglePassword.addEventListener('click', function() {
                const passwordInput = document.getElementById('loginPassword');
                const icon = this.querySelector('i');

                if (passwordInput.type === 'password') {
                    passwordInput.type = 'text';
                    icon.className = 'bi bi-eye-slash';
                } else {
                    passwordInput.type = 'password';
                    icon.className = 'bi bi-eye';
                }
            });
        }
    }

    // お問い合わせモーダル
    const contactModal = document.getElementById('contactModal');
    if (contactModal) {
        contactModal.addEventListener('shown.bs.modal', function() {
            document.getElementById('contactName')?.focus();
        });
    }
}

// スクロールイベント
function setupScrollEvents() {
    let ticking = false;

    window.addEventListener('scroll', function() {
        if (!ticking) {
            requestAnimationFrame(function() {
                handleScroll();
                ticking = false;
            });
            ticking = true;
        }
    });
}

// スクロール処理
function handleScroll() {
    const scrollTop = window.pageYOffset;

    // ナビゲーション背景の変更
    const navbar = document.querySelector('.navbar');
    if (scrollTop > 100) {
        navbar.classList.add('scrolled');
    } else {
        navbar.classList.remove('scrolled');
    }

    // Back to Top ボタンの表示/非表示
    const backToTopBtn = document.getElementById('backToTop');
    if (backToTopBtn) {
        if (scrollTop > 300) {
            backToTopBtn.style.display = 'block';
        } else {
            backToTopBtn.style.display = 'none';
        }
    }

    // スクロール時のアニメーション
    animateOnScroll();
}

// スクロールアニメーション
function animateOnScroll() {
    const elements = document.querySelectorAll('.animate-on-scroll');
    elements.forEach(element => {
        const elementTop = element.getBoundingClientRect().top;
        const elementVisible = 150;

        if (elementTop < window.innerHeight - elementVisible) {
            element.classList.add('animated');
        }
    });
}

// フォームイベント
function setupFormEvents() {
    // フォーム送信処理
    document.querySelectorAll('form').forEach(form => {
        form.addEventListener('submit', function(e) {
            e.preventDefault();
            handleFormSubmit(this);
        });
    });
}

// UI要素の初期化
function initializeUI() {
    // カウンターアニメーション
    animateCounters();

    // レンダリング
    renderProducts();
    updateCartDisplay();
    updateWishlistDisplay();
}

// 初期データ読み込み
function loadInitialData() {
    // ローカルストレージからデータを読み込み
    const savedCart = localStorage.getItem('bootstrap-cart');
    const savedWishlist = localStorage.getItem('bootstrap-wishlist');

    if (savedCart) {
        try {
            cart = JSON.parse(savedCart);
        } catch (e) {
            console.error('カートデータの読み込みに失敗:', e);
        }
    }

    if (savedWishlist) {
        try {
            wishlist = JSON.parse(savedWishlist);
        } catch (e) {
            console.error('ウィッシュリストデータの読み込みに失敗:', e);
        }
    }

    console.log('💾 データ読み込み完了');
}

// 商品レンダリング
function renderProducts() {
    const container = document.getElementById('productsGrid');
    if (!container) return;

    let filteredProducts = [...PRODUCTS_DB];

    // フィルタリング
    if (currentFilter && currentFilter !== 'all') {
        filteredProducts = filteredProducts.filter(product => 
            product.category.toLowerCase().includes(currentFilter)
        );
    }

    // ソート
    filteredProducts = sortProducts(filteredProducts, currentSort);

    // HTML生成
    const productsHTML = filteredProducts.map(product => generateProductCard(product)).join('');

    container.innerHTML = productsHTML;

    // カードにアニメーションクラスを追加
    container.querySelectorAll('.card').forEach((card, index) => {
        card.style.animationDelay = `${index * 0.1}s`;
        card.classList.add('animate-on-scroll');
    });

    console.log(`🏪 商品 ${filteredProducts.length} 件をレンダリング`);
}

// 商品カード生成
function generateProductCard(product) {
    const isInWishlist = wishlist.some(item => item.id === product.id);
    const isInCart = cart.some(item => item.id === product.id);
    const discountPercent = product.originalPrice ? 
        Math.round((product.originalPrice - product.price) / product.originalPrice * 100) : 0;

    return `
        <div class="col-lg-4 col-md-6 mb-4">
            <div class="card product-card h-100">
                ${product.badge ? `<span class="badge bg-${getBadgeColor(product.badge)} product-badge">${product.badge}</span>` : ''}

                <div class="product-actions">
                    <button class="btn btn-light btn-sm rounded-circle me-2" 
                            onclick="toggleWishlist(${product.id})" 
                            title="ウィッシュリストに追加">
                        <i class="bi bi-heart${isInWishlist ? '-fill text-danger' : ''}"></i>
                    </button>
                    <button class="btn btn-light btn-sm rounded-circle" 
                            onclick="quickView(${product.id})" 
                            title="クイックビュー">
                        <i class="bi bi-eye"></i>
                    </button>
                </div>

                <img src="${product.image}" class="card-img-top" alt="${product.name}">

                <div class="card-body d-flex flex-column">
                    <h5 class="card-title">${product.name}</h5>
                    <p class="card-text text-muted flex-grow-1">${product.description}</p>

                    <div class="product-rating mb-2">
                        ${generateStarRating(product.rating)}
                        <small class="text-muted ms-2">(${product.reviews})</small>
                    </div>

                    <div class="d-flex justify-content-between align-items-center mb-3">
                        <div>
                            ${product.originalPrice ? 
                                `<span class="text-decoration-line-through text-muted me-2">¥${product.originalPrice.toLocaleString()}</span>
                                 <span class="badge bg-danger me-2">${discountPercent}% OFF</span>` : ''}
                            <span class="h5 text-primary mb-0">¥${product.price.toLocaleString()}</span>
                        </div>
                    </div>

                    <div class="d-grid gap-2">
                        <button class="btn btn-primary ${!product.inStock ? 'disabled' : ''}" 
                                onclick="addToCart(${product.id})" 
                                ${!product.inStock ? 'disabled' : ''}>
                            <i class="bi bi-cart-plus"></i> 
                            ${isInCart ? 'カートに追加済み' : product.inStock ? 'カートに追加' : '在庫切れ'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    `;
}

// 星評価生成
function generateStarRating(rating) {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;
    const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

    let starsHTML = '';

    // フル星
    for (let i = 0; i < fullStars; i++) {
        starsHTML += '<i class="bi bi-star-fill"></i>';
    }

    // ハーフ星
    if (hasHalfStar) {
        starsHTML += '<i class="bi bi-star-half"></i>';
    }

    // 空星
    for (let i = 0; i < emptyStars; i++) {
        starsHTML += '<i class="bi bi-star"></i>';
    }

    return starsHTML;
}

// バッジ色取得
function getBadgeColor(badge) {
    const colors = {
        'SALE': 'danger',
        'NEW': 'success',
        'HOT': 'warning',
        'PREMIUM': 'info'
    };
    return colors[badge] || 'secondary';
}

// 商品ソート
function sortProducts(products, sortType) {
    switch (sortType) {
        case 'price-low':
            return products.sort((a, b) => a.price - b.price);
        case 'price-high':
            return products.sort((a, b) => b.price - a.price);
        case 'name':
            return products.sort((a, b) => a.name.localeCompare(b.name));
        case 'rating':
            return products.sort((a, b) => b.rating - a.rating);
        default:
            return products;
    }
}

// カート操作
function addToCart(productId) {
    const product = PRODUCTS_DB.find(p => p.id === productId);
    if (!product || !product.inStock) return;

    const existingItem = cart.find(item => item.id === productId);

    if (existingItem) {
        existingItem.quantity += 1;
        showToast('商品数量を増やしました', 'success');
    } else {
        cart.push({
            id: product.id,
            name: product.name,
            price: product.price,
            image: product.image,
            quantity: 1
        });
        showToast(`${product.name}をカートに追加しました`, 'success');
    }

    updateCartDisplay();
    saveCartToStorage();
    renderProducts(); // ボタン状態更新のため再描画

    console.log(`🛒 商品をカートに追加: ${product.name}`);
}

function removeFromCart(productId) {
    cart = cart.filter(item => item.id !== productId);
    updateCartDisplay();
    saveCartToStorage();
    renderProducts();
}

function updateCartQuantity(productId, newQuantity) {
    if (newQuantity <= 0) {
        removeFromCart(productId);
        return;
    }

    const item = cart.find(item => item.id === productId);
    if (item) {
        item.quantity = newQuantity;
        updateCartDisplay();
        saveCartToStorage();
    }
}

// カート表示更新
function updateCartDisplay() {
    const cartCount = document.getElementById('cartCount');
    const cartItems = document.getElementById('cartItems');
    const cartTotal = document.getElementById('cartTotal');

    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
    const totalPrice = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

    if (cartCount) {
        cartCount.textContent = totalItems;
        // アニメーション効果
        cartCount.style.transform = 'scale(1.3)';
        setTimeout(() => {
            cartCount.style.transform = 'scale(1)';
        }, 200);
    }

    if (cartTotal) {
        cartTotal.textContent = `¥${totalPrice.toLocaleString()}`;
    }

    if (cartItems) {
        if (cart.length === 0) {
            cartItems.innerHTML = `
                <div class="text-center text-muted">
                    <i class="bi bi-cart-x display-4 mb-3"></i>
                    <p>カートは空です</p>
                </div>
            `;
        } else {
            cartItems.innerHTML = cart.map(item => `
                <div class="d-flex align-items-center border-bottom pb-3 mb-3">
                    <img src="${item.image}" alt="${item.name}" class="me-3" 
                         style="width: 60px; height: 60px; object-fit: cover; border-radius: 8px;">
                    <div class="flex-grow-1">
                        <h6 class="mb-0">${item.name}</h6>
                        <small class="text-muted">¥${item.price.toLocaleString()}</small>
                    </div>
                    <div class="d-flex align-items-center">
                        <button class="btn btn-outline-secondary btn-sm" 
                                onclick="updateCartQuantity(${item.id}, ${item.quantity - 1})">
                            <i class="bi bi-dash"></i>
                        </button>
                        <span class="mx-3">${item.quantity}</span>
                        <button class="btn btn-outline-secondary btn-sm" 
                                onclick="updateCartQuantity(${item.id}, ${item.quantity + 1})">
                            <i class="bi bi-plus"></i>
                        </button>
                        <button class="btn btn-outline-danger btn-sm ms-2" 
                                onclick="removeFromCart(${item.id})">
                            <i class="bi bi-trash"></i>
                        </button>
                    </div>
                </div>
            `).join('');
        }
    }
}

// ウィッシュリスト操作
function toggleWishlist(productId) {
    const product = PRODUCTS_DB.find(p => p.id === productId);
    if (!product) return;

    const existingIndex = wishlist.findIndex(item => item.id === productId);

    if (existingIndex >= 0) {
        wishlist.splice(existingIndex, 1);
        showToast('ウィッシュリストから削除しました', 'info');
    } else {
        wishlist.push({
            id: product.id,
            name: product.name,
            price: product.price,
            image: product.image
        });
        showToast(`${product.name}をウィッシュリストに追加しました`, 'success');
    }

    updateWishlistDisplay();
    saveWishlistToStorage();
    renderProducts(); // ハートアイコン更新のため再描画
}

// ウィッシュリスト表示更新
function updateWishlistDisplay() {
    const wishlistCount = document.getElementById('wishlistCount');
    const wishlistItems = document.getElementById('wishlistItems');

    if (wishlistCount) {
        wishlistCount.textContent = wishlist.length;
    }

    if (wishlistItems) {
        if (wishlist.length === 0) {
            wishlistItems.innerHTML = `
                <div class="text-center text-muted">
                    <i class="bi bi-heart display-4 mb-3"></i>
                    <p>ウィッシュリストは空です</p>
                </div>
            `;
        } else {
            wishlistItems.innerHTML = wishlist.map(item => `
                <div class="d-flex align-items-center border-bottom pb-3 mb-3">
                    <img src="${item.image}" alt="${item.name}" class="me-3" 
                         style="width: 60px; height: 60px; object-fit: cover; border-radius: 8px;">
                    <div class="flex-grow-1">
                        <h6 class="mb-0">${item.name}</h6>
                        <small class="text-primary">¥${item.price.toLocaleString()}</small>
                    </div>
                    <div>
                        <button class="btn btn-primary btn-sm me-2" 
                                onclick="addToCart(${item.id})">
                            <i class="bi bi-cart-plus"></i>
                        </button>
                        <button class="btn btn-outline-danger btn-sm" 
                                onclick="toggleWishlist(${item.id})">
                            <i class="bi bi-trash"></i>
                        </button>
                    </div>
                </div>
            `).join('');
        }
    }
}

// データ保存
function saveCartToStorage() {
    try {
        localStorage.setItem('bootstrap-cart', JSON.stringify(cart));
    } catch (e) {
        console.error('カートデータの保存に失敗:', e);
    }
}

function saveWishlistToStorage() {
    try {
        localStorage.setItem('bootstrap-wishlist', JSON.stringify(wishlist));
    } catch (e) {
        console.error('ウィッシュリストデータの保存に失敗:', e);
    }
}

// 検索機能
function performSearch(query) {
    if (!query.trim()) {
        renderProducts();
        return;
    }

    const results = PRODUCTS_DB.filter(product => 
        product.name.toLowerCase().includes(query.toLowerCase()) ||
        product.description.toLowerCase().includes(query.toLowerCase()) ||
        product.category.toLowerCase().includes(query.toLowerCase())
    );

    const container = document.getElementById('productsGrid');
    if (container) {
        if (results.length === 0) {
            container.innerHTML = `
                <div class="col-12 text-center py-5">
                    <i class="bi bi-search display-1 text-muted mb-3"></i>
                    <h3 class="text-muted">検索結果が見つかりません</h3>
                    <p class="text-muted">「${query}」に一致する商品が見つかりませんでした。</p>
                </div>
            `;
        } else {
            const resultsHTML = results.map(product => generateProductCard(product)).join('');
            container.innerHTML = resultsHTML;
        }
    }

    console.log(`🔍 検索実行: "${query}" (${results.length}件の結果)`);
}

// トースト表示
function showToast(message, type = 'info') {
    const toastContainer = document.getElementById('toastContainer');
    if (!toastContainer) return;

    const toastId = 'toast_' + Date.now();
    const iconMap = {
        success: 'bi-check-circle-fill text-success',
        error: 'bi-x-circle-fill text-danger',
        warning: 'bi-exclamation-triangle-fill text-warning',
        info: 'bi-info-circle-fill text-info'
    };

    const toastHTML = `
        <div id="${toastId}" class="toast" role="alert" aria-live="assertive" aria-atomic="true">
            <div class="toast-header">
                <i class="bi ${iconMap[type]} me-2"></i>
                <strong class="me-auto">通知</strong>
                <small class="text-muted">たった今</small>
                <button type="button" class="btn-close" data-bs-dismiss="toast"></button>
            </div>
            <div class="toast-body">
                ${message}
            </div>
        </div>
    `;

    toastContainer.insertAdjacentHTML('beforeend', toastHTML);

    const toastElement = document.getElementById(toastId);
    const toast = new bootstrap.Toast(toastElement, {
        autohide: true,
        delay: 4000
    });

    toast.show();

    // トースト非表示後に要素を削除
    toastElement.addEventListener('hidden.bs.toast', function() {
        this.remove();
    });
}

// クイックビュー
