    window.addEventListener('load', function() {
        setTimeout(() => {
            const perfData = performance.getEntriesByType('navigation')[0];
            const loadTime = perfData.loadEventEnd - perfData.loadEventStart;

            console.log('📊 パフォーマンス情報:');
            console.log(`  - DOMContentLoaded: ${Math.round(perfData.domContentLoadedEventEnd)}ms`);
            console.log(`  - ページ読み込み完了: ${Math.round(perfData.loadEventEnd)}ms`);
            console.log(`  - リソース読み込み: ${Math.round(loadTime)}ms`);

            // 使用中のBootstrapコンポーネントをログ出力
            const components = {
                'Navbar': document.querySelector('.navbar') !== null,
                'Carousel': document.querySelector('.carousel') !== null,
                'Modal': document.querySelectorAll('.modal').length,
                'Offcanvas': document.querySelectorAll('.offcanvas').length,
                'Cards': document.querySelectorAll('.card').length,
                'Buttons': document.querySelectorAll('.btn').length,
                'Forms': document.querySelectorAll('.form-control').length,
                'Dropdowns': document.querySelectorAll('.dropdown').length
            };

            console.log('🎨 Bootstrapコンポーネント使用状況:', components);

            // メモリ使用量（対応ブラウザのみ）
            if ('memory' in performance) {
                console.log('💾 メモリ使用量:', {
                    used: Math.round(performance.memory.usedJSHeapSize / 1048576) + 'MB',
                    total: Math.round(performance.memory.totalJSHeapSize / 1048576) + 'MB'
                });
            }
        }, 1000);
    });
}

