css.expected:
/* 商品ギャラリー統合システム CSS */
body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%);
    min-height: 100vh;
}

.gallery-item {
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
    transition: all 0.3s ease;
    cursor: pointer;
}

.gallery-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0,0,0,0.2);
}

.gallery-image {
    width: 100%;
    height: 250px;
    object-fit: cover;
}

@media (max-width: 768px) {
    .container {
        padding: 15px;
    }
}

js.template:
// JavaScript実装エリア

// ここに実装してください
console.log('課題実装を開始してください');

js.expected:
// 商品ギャラリー統合システム JavaScript

document.addEventListener('DOMContentLoaded', function() {
    console.log('商品ギャラリー統合システム開始');

    initializeGallerySystem();

    console.log('ギャラリーシステム初期化完了');
});

// ギャラリーデータ
const galleryData = [
    {
        id: 1,
        title: 'プレミアムノートPC',
        image: 'https://via.placeholder.com/400x300/007bff/ffffff?text=Premium+Laptop',
        category: 'electronics',
        price: 198000
    },
    {
        id: 2,
        title: 'デザイナーズソファ',
        image: 'https://via.placeholder.com/400x300/28a745/ffffff?text=Designer+Sofa',
        category: 'furniture',
        price: 158000
    }
];

function initializeGallerySystem() {
    const container = document.getElementById('galleryContainer');

    if (container) {
        displayGallery(galleryData, container);
    }
}

function displayGallery(data, container) {
    const galleryHtml = data.map(item => `
        <div class="col-md-6 mb-4">
            <div class="gallery-item card">
                <img src="${item.image}" class="gallery-image card-img-top" alt="${item.title}">
                <div class="card-body">
                    <h5 class="card-title">${item.title}</h5>
                    <p class="card-text">¥${item.price.toLocaleString()}</p>
                </div>
            </div>
        </div>
    `).join('');

    container.innerHTML = `<div class="row">${galleryHtml}</div>`;
}

// エクスポート（モジュールシステム対応）
if (typeof module !== 'undefined' && module.exports) {
    module.exports = { initializeGallerySystem };
}
