css.expected:
/* 中央寄せ - text-alignとmargin: 0 autoの基本 */

/* 基本スタイル */
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 20px;
    background-color: #f5f5f5;
}

h1 {
    text-align: center;
    color: darkblue;
    border-bottom: 3px solid blue;
    padding-bottom: 15px;
    margin-bottom: 40px;
}

h2 {
    color: darkgreen;
    margin-top: 40px;
    margin-bottom: 20px;
    border-left: 4px solid green;
    padding-left: 15px;
}

h3 {
    margin-top: 0;
    color: darkred;
}

/* 1. テキストの中央寄せ */
.center-text {
    text-align: center;
    background-color: #e8f5e8;
    padding: 15px;
    margin: 10px 0;
    border: 1px solid #4CAF50;
}

.center-image {
    display: block;
    margin: 20px auto;
    width: 80px;
    height: 80px;
}

/* 2. ボックスの中央寄せ */
.center-box {
    width: 400px;
    margin: 0 auto;
    background-color: #fff3cd;
    padding: 20px;
    border: 2px solid #ffc107;
    margin-top: 20px;
    margin-bottom: 20px;
}

/* 3. 両方の組み合わせ */
.center-everything {
    width: 350px;
    margin: 0 auto;
    text-align: center;
    background-color: #d1ecf1;
    padding: 25px;
    border: 2px solid #17a2b8;
    margin-top: 20px;
    margin-bottom: 20px;
    border-radius: 8px;
}

/* 4. 実用例 */
.card {
    width: 300px;
    margin: 30px auto;
    background-color: white;
    padding: 25px;
    border: 1px solid #ddd;
    border-radius: 10px;
    text-align: center;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.card h3 {
    color: #333;
    margin-bottom: 15px;
}

.card p {
    color: #666;
    margin: 0;
}
