css.expected:
/* ホバー効果の最小実装 */
/* 基本: hover + transition */
.hover-item {
  transition: all 0.3s ease;
}
.scale:hover {
  transform: scale(1.1);
}
.rotate:hover {
  transform: rotate(5deg);
}

/* 実用例: ボタン */
.btn {
  transition: all 0.3s ease;
}
.btn:hover {
  transform: translateY(-2px);
}

/* 実用例: カード */
.card {
  transition: all 0.3s ease;
}
.card:hover {
  transform: translateY(-4px);
}
