    margin: 20px 0;
    border: 2px dashed #dee2e6;
    min-height: 140px;
}

.button-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 20px;
    margin: 20px 0;
}

.select-btn {
    padding: 18px 25px;
    font-size: 1.1rem;
    border: none;
    border-radius: 12px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.btn-quote {
    background: linear-gradient(135deg, #27ae60, #2ecc71);
    color: white;
}

.btn-color {
    background: linear-gradient(135deg, #8e44ad, #9b59b6);
    color: white;
}

.btn-food {
    background: linear-gradient(135deg, #e67e22, #f39c12);
    color: white;
}

.select-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.25);
}

.select-btn:active {
    transform: translateY(-1px);
}

.stats-section {
    background: #f1f8e9;
    padding: 20px;
    border-radius: 10px;
    margin: 25px 0;
    border: 1px solid #c8e6c9;
}

.stats-section h4 {
    margin-top: 0;
    color: #2e7d32;
    text-align: center;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 15px;
    margin-top: 15px;
}

.stat-card {
    background: white;
    padding: 15px;
    border-radius: 8px;
    text-align: center;
    border: 1px solid #a5d6a7;
}

.stat-label {
    font-size: 0.9rem;
    color: #4a90e2;
    margin-bottom: 5px;
}

.stat-number {
    font-size: 2rem;
    font-weight: bold;
    color: #2e7d32;
}

.arrays-display {
    background: #fce4ec;
    padding: 20px;
    border-radius: 10px;
    margin: 25px 0;
}

.arrays-display h4 {
    margin-top: 0;
    color: #ad1457;
    text-align: center;
}

.array-list {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 15px;
}

.array-info {
    background: white;
    padding: 15px;
    border-radius: 8px;
    border: 1px solid #f8bbd9;
}

.array-info h4 {
    margin: 0 0 10px 0;
    color: #c2185b;
    font-size: 1rem;
}

.array-info div {
    font-size: 0.95rem;
    color: #666;
}

.code-example {
    background: #263238;
    color: #e0f2f1;
    padding: 20px;
    border-radius: 8px;
    font-family: 'Courier New', monospace;
    white-space: pre-wrap;
    overflow-x: auto;
    margin: 15px 0;
    line-height: 1.5;
}

.instruction {
    background: #e8f5e9;
    padding: 20px;
    border-radius: 10px;
    border-left: 4px solid #4caf50;
}

.instruction h3 {
    margin-top: 0;
    color: #2e7d32;
}

