js.expected:
// 最小ロジック：オブジェクト管理 + いいね + 表示/リセット
let productData={name:'プレミアム Tシャツ',price:2980,likes:0,isLiked:false};

const likeBtn=document.getElementById('likeBtn');
const likeCount=document.getElementById('likeCount');
const totalLikes=document.getElementById('totalLikes');
const objectDisplay=document.getElementById('objectDisplay');
const resetBtn=document.getElementById('resetBtn');

const displayObject=()=>{
  objectDisplay.textContent=JSON.stringify(productData,null,2);
};

const updateUI=()=>{
  likeCount.textContent=productData.likes;
  totalLikes.textContent=productData.likes;
  likeBtn.classList.toggle('liked',productData.isLiked);
  displayObject();
};

likeBtn.addEventListener('click',()=>{
  productData.isLiked=!productData.isLiked;
  productData.likes=productData.isLiked?1:0;
  updateUI();
});

resetBtn.addEventListener('click',()=>{
  productData={name:'プレミアム Tシャツ',price:2980,likes:0,isLiked:false};
  updateUI();
});

updateUI();
