* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Hiragino Sans', 'Yu Gothic UI', sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
}

.container {
    max-width: 1100px;
    width: 100%;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 25px;
}

/* å•†å“ã‚«ãƒ¼ãƒ‰ */
.product-card {
    background: white;
    border-radius: 20px;
    padding: 25px;
    text-align: center;
    box-shadow: 0 20px 40px rgba(0,0,0,0.1);
}

.product-image {
    width: 100px;
    height: 100px;
    background: linear-gradient(45deg, #ff6b6b, #4ecdc4);
    border-radius: 15px;
    margin: 0 auto 15px auto;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 35px;
    color: white;
}

.product-info h2 {
    color: #333;
    font-size: 18px;
    margin-bottom: 5px;
}

.product-price {
    font-size: 20px;
    color: #e74c3c;
    font-weight: bold;
    margin-bottom: 15px;
}

/* ã‚¢ã‚¯ã‚·ãƒ§ãƒ³ãƒœã‚¿ãƒ³ */
.action-buttons {
    display: flex;
    gap: 8px;
    margin-bottom: 15px;
    justify-content: center;
}

.btn {
    padding: 8px 15px;
    border: none;
    border-radius: 18px;
    cursor: pointer;
    font-size: 12px;
    font-weight: 600;
    transition: all 0.3s ease;
    flex: 1;
    max-width: 90px;
}

.btn-like {
    background: #f8f9fa;
    color: #666;
    border: 2px solid #ddd;
}

.btn-like.liked {
    background: #e74c3c;
    color: white;
    border-color: #e74c3c;
}

.btn-details {
    background: #3498db;
    color: white;
}

.btn-cart {
    background: #27ae60;
    color: white;
}

.btn-cart.in-cart {
    background: #f39c12;
}

.btn:hover {
    transform: scale(1.05);
}

/* å•†å“è©³ç´° */
.product-details {
    background: #f8f9fa;
    border-radius: 10px;
    padding: 12px;
    margin-top: 10px;
    display: none;
    text-align: left;
    font-size: 12px;
}

.product-details.show {
    display: block;
    animation: fadeIn 0.3s ease;
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

.product-details h3 {
    color: #333;
    margin-bottom: 8px;
    font-size: 14px;
}

.product-details p {
    color: #666;
    line-height: 1.4;
    margin-bottom: 3px;
}

/* å­¦ç¿’ãƒ‘ãƒãƒ« */
.learning-panel {
    background: white;
    border-radius: 20px;
    padding: 20px;
    box-shadow: 0 20px 40px rgba(0,0,0,0.1);
}

.panel-title {
    text-align: center;
    font-size: 16px;
    font-weight: 600;
    color: #333;
    margin-bottom: 15px;
}

/* çµ±è¨ˆè¡¨ç¤ºï¼ˆã‚³ãƒ³ãƒ‘ã‚¯ãƒˆç‰ˆï¼‰ */
.stats-grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 8px;
    margin-bottom: 15px;
}

.stat-item {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 10px;
    border-radius: 10px;
    text-align: center;
}

.stat-number {
    font-size: 16px;
    font-weight: bold;
    margin-bottom: 2px;
}

.stat-label {
    font-size: 9px;
    opacity: 0.9;
}

/* localStorageçŠ¶æ…‹è¡¨ç¤º */
.storage-status {
    background: #2ecc71;
    color: white;
    border-radius: 8px;
    padding: 8px;
    margin-bottom: 12px;
    text-align: center;
    font-size: 11px;
    font-weight: 600;
}

.storage-status.error {
    background: #e74c3c;
}

/* å±¥æ­´è¡¨ç¤ºã‚¨ãƒªã‚¢ */
.history-section {
    margin-bottom: 12px;
}

.history-title {
    font-size: 12px;
    color: #666;
    margin-bottom: 8px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.btn-clear-history {
    background: #95a5a6;
    color: white;
    border: none;
    padding: 3px 8px;
    border-radius: 8px;
    font-size: 9px;
    cursor: pointer;
}

.history-list {
    background: #f8f9fa;
    border-radius: 8px;
    padding: 8px;
    max-height: 120px;
    overflow-y: auto;
    font-size: 10px;
}

.history-item {
    color: #666;
    margin-bottom: 3px;
    padding: 2px;
    border-left: 2px solid #3498db;
    padding-left: 6px;
}

/* ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆè¡¨ç¤ºï¼ˆã‚³ãƒ³ãƒ‘ã‚¯ãƒˆç‰ˆï¼‰ */
.object-display {
    background: #34495e;
    color: #ecf0f1;
    border-radius: 8px;
    padding: 10px;
    margin-bottom: 12px;
    font-family: 'Courier New', monospace;
    font-size: 10px;
}

.object-title {
    color: #e74c3c;
    font-weight: bold;
    margin-bottom: 6px;
}

/* ã‚³ãƒ³ãƒˆãƒ­ãƒ¼ãƒ«ãƒœã‚¿ãƒ³ */
.control-buttons {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 8px;
}

.btn-control {
    padding: 8px;
    border: none;
    border-radius: 8px;
    font-size: 11px;
    font-weight: 600;
    cursor: pointer;
    transition: background 0.3s ease;
}

.btn-reset {
    background: #e74c3c;
    color: white;
}

.btn-export {
    background: #27ae60;
    color: white;
}

.btn-control:hover {
    opacity: 0.8;
}

/* ãƒ¬ã‚¹ãƒãƒ³ã‚·ãƒ– */
@media (max-width: 768px) {
    .container {
        grid-template-columns: 1fr;
        gap: 15px;
    }

    .stats-grid {
        grid-template-columns: 1fr 1fr;
    }
}

