css.expected:
/* 初めてのJavaScript - シンプルなボタンクリック学習用スタイル */

/* 基本スタイル */
body {
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 20px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #333;
}

.container {
    width: 100%;
    max-width: 600px;
    text-align: center;
}

h1 {
    color: white;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 30px;
    margin: 0 0 40px 0;
    border-radius: 20px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
    font-size: 32px;
    font-weight: bold;
}

/* ウェルカムセクション */
.welcome-section {
    background: rgba(255, 255, 255, 0.95);
    border-radius: 20px;
    padding: 50px 30px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.15);
    margin-bottom: 30px;
}

.welcome-text {
    font-size: 18px;
    color: #555;
    margin: 0 0 30px 0;
    line-height: 1.6;
}

/* メインボタンスタイル */
.hello-button {
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
    border: none;
    padding: 20px 40px;
    border-radius: 30px;
    font-size: 20px;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
    position: relative;
    overflow: hidden;
}

.hello-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 35px rgba(102, 126, 234, 0.4);
    background: linear-gradient(135deg, #5a6fd8, #6a4190);
}

.hello-button:active {
    transform: translateY(-1px);
    box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
}

/* クリック時のアニメーション効果 */
.hello-button::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width 0.3s ease, height 0.3s ease;
}

.hello-button:active::before {
    width: 100px;
    height: 100px;
}

/* レスポンシブデザイン */
@media (max-width: 768px) {
    body {
        padding: 15px;
    }

    .container {
        max-width: 100%;
    }

    h1 {
        font-size: 26px;
        padding: 25px 20px;
        margin-bottom: 30px;
    }

    .welcome-section {
        padding: 40px 20px;
        margin-bottom: 20px;
    }

    .welcome-text {
        font-size: 16px;
    }

    .hello-button {
        padding: 18px 35px;
        font-size: 18px;
        width: 100%;
        max-width: 300px;
    }
}

@media (max-width: 480px) {
    h1 {
        font-size: 22px;
        padding: 20px 15px;
    }

    .welcome-section {
        padding: 30px 15px;
    }

    .welcome-text {
        font-size: 15px;
        margin-bottom: 25px;
    }

    .hello-button {
        padding: 16px 30px;
        font-size: 16px;
    }
}

/* ホバー時のパルス効果 */
@keyframes pulse {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.02);
    }
    100% {
        transform: scale(1);
    }
}

.hello-button:hover {
    animation: pulse 1s infinite;
}

