css.expected:
body {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    max-width: 800px;
    margin: 40px auto;
    padding: 20px;
    line-height: 1.6;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    min-height: 100vh;
}

.container {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    padding: 40px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

h1 {
    color: #FFE135;
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 10px;
}

.step-info {
    text-align: center;
    margin-bottom: 30px;
    opacity: 0.9;
}

.welcome-message {
    background: rgba(255, 255, 255, 0.2);
    padding: 30px;
    border-radius: 15px;
    text-align: center;
    margin: 30px 0;
    font-size: 1.2rem;
}

.highlight {
    color: #FFE135;
    font-weight: bold;
}

.celebration {
    font-size: 3rem;
    text-align: center;
    margin: 20px 0;
}

