css.expected:
body {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    max-width: 900px;
    margin: 40px auto;
    padding: 20px;
    line-height: 1.6;
    background: linear-gradient(135deg, #ff9a9e 0%, #fecfef 50%, #fecfef 100%);
    color: #2c3e50;
    min-height: 100vh;
}

.container {
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    padding: 40px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

h1 {
    color: #e74c3c;
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 10px;
}

.step-info {
    text-align: center;
    margin-bottom: 30px;
    opacity: 0.8;
    color: #7f8c8d;
}

.info-box {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 25px;
    border-radius: 15px;
    margin: 20px 0;
    border-left: 5px solid #e74c3c;
}

.calculation-box {
    background: #f8f9fa;
    padding: 20px;
    border-radius: 10px;
    border: 2px solid #3498db;
    margin: 15px 0;
}

.highlight {
    color: #e74c3c;
    font-weight: bold;
}

.code-example {
    background: #2c3e50;
    color: #ecf0f1;
    padding: 15px;
    border-radius: 8px;
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    margin: 15px 0;
    border: 1px solid #34495e;
}

.instruction {
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    color: white;
    padding: 20px;
    border-radius: 10px;
    margin: 20px 0;
}

.celebration {
    font-size: 2.5rem;
    text-align: center;
    margin: 20px 0;
}

.math-symbol {
    font-size: 1.5rem;
    color: #e74c3c;
    font-weight: bold;
}

.result-display {
    background: linear-gradient(135deg, #a8edea 0%, #fed6e3 100%);
    padding: 15px;
    border-radius: 10px;
    text-align: center;
    margin: 10px 0;
    font-weight: bold;
    color: #2c3e50;
}

js.template:
// JavaScript実装エリア

// ここに実装してください
console.log('課題実装を開始してください');

js.expected:
console.log("🧮 基本計算を開始します！");
console.log("================================");

// 基本の四則演算
let number1 = 10;
let number2 = 3;

console.log("使用する数値:");
console.log("number1 = " + number1);
console.log("number2 = " + number2);
console.log("");

// 足し算
let addition = number1 + number2;
console.log("足し算: " + number1 + " + " + number2 + " = " + addition);

// 引き算
let subtraction = number1 - number2;
console.log("引き算: " + number1 + " - " + number2 + " = " + subtraction);

// 掛け算
let multiplication = number1 * number2;
console.log("掛け算: " + number1 + " × " + number2 + " = " + multiplication);

// 割り算
let division = number1 / number2;
console.log("割り算: " + number1 + " ÷ " + number2 + " = " + division.toFixed(2));

console.log("");
console.log("🎉 すべての計算が完了しました！");

// 計算結果をページにも表示
document.getElementById('calculation-results').innerHTML = `
    <div class="result-display">足し算: ${number1} + ${number2} = ${addition}</div>
    <div class="result-display">引き算: ${number1} - ${number2} = ${subtraction}</div>
    <div class="result-display">掛け算: ${number1} × ${number2} = ${multiplication}</div>
    <div class="result-display">割り算: ${number1} ÷ ${number2} = ${division.toFixed(2)}</div>
`;
