css.template:
/* 基本的なスタイル（最小） */
body { font-family: system-ui, -apple-system, "Segoe UI", sans-serif; margin: 16px; }
#message-display { margin: 8px 0; }
button { margin-right: 6px; }

js.template:
// ボタンクリックイベントの基本実装

// 1. Helloボタンの処理
let helloButton = document.getElementById('hello-button');
helloButton.addEventListener('click', function() {
    let display = document.getElementById('message-display');
    display.textContent = 'Hello! ボタンがクリックされました！';
});

// 2. 色変更ボタンの処理
let colorButton = document.getElementById('color-button');
colorButton.addEventListener('click', function() {
    let display = document.getElementById('message-display');
    display.textContent = '色が変わりました！';
    display.style.background = '#ffeb3b';
});

// 3. 時間表示ボタンの処理
let timeButton = document.getElementById('time-button');
timeButton.addEventListener('click', function() {
    let display = document.getElementById('message-display');
    let now = new Date();
    let timeString = now.getHours() + ':' + now.getMinutes() + ':' + now.getSeconds();
    display.textContent = '現在時刻: ' + timeString;
});
