    font-weight: bold;
}

.counter-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);
}

.counter-btn:active {
    transform: translateY(0);
}

.counter-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
}

.btn-plus1 { background: linear-gradient(135deg, #56ab2f, #a8e6cf); }
.btn-plus5 { background: linear-gradient(135deg, #4CAF50, #8BC34A); }
.btn-plus10 { background: linear-gradient(135deg, #2E7D32, #66BB6A); }
.btn-minus1 { background: linear-gradient(135deg, #ff416c, #ff4b2b); }
.btn-minus5 { background: linear-gradient(135deg, #F44336, #E91E63); }
.btn-minus10 { background: linear-gradient(135deg, #B71C1C, #D32F2F); }
.btn-reset { background: linear-gradient(135deg, #667eea, #764ba2); }
.btn-random { background: linear-gradient(135deg, #ff9a9e, #fecfef); }

.info-box {
    background: #f8f9fa;
    padding: 25px;
    border-radius: 15px;
    margin: 20px 0;
    border-left: 5px solid #3498db;
}

.highlight {
    color: #e74c3c;
    font-weight: bold;
}

.code-example {
    background: #2c3e50;
    color: #ecf0f1;
    padding: 15px;
    border-radius: 8px;
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    margin: 15px 0;
    border: 1px solid #34495e;
}

.instruction {
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    color: white;
    padding: 20px;
    border-radius: 10px;
    margin: 20px 0;
}

.celebration {
    font-size: 3rem;
    text-align: center;
    margin: 20px 0;
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 15px;
    margin: 20px 0;
}

.feature-card {
    background: rgba(0, 0, 0, 0.1);
    padding: 20px;
    border-radius: 15px;
    text-align: center;
}

.feature-icon {
    font-size: 2.5rem;
    margin-bottom: 10px;
}

.status-display {
    padding: 15px;
    border-radius: 10px;
    margin: 15px 0;
    text-align: center;
    font-weight: bold;
    font-size: 1.1rem;
}

.status-normal { background: linear-gradient(135deg, #a8edea, #fed6e3); color: #2c3e50; }
.status-warning { background: linear-gradient(135deg, #ffeaa7, #fab1a0); color: #2c3e50; }
.status-danger { background: linear-gradient(135deg, #ff7675, #fd79a8); color: white; }
.status-success { background: linear-gradient(135deg, #00b894, #00cec9); color: white; }

.progress-section {
    background: rgba(255, 255, 255, 0.1);
    padding: 20px;
    border-radius: 15px;
    margin: 20px 0;
}

.progress-bar {
    width: 100%;
    height: 20px;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 10px;
    overflow: hidden;
    margin: 10px 0;
}

.progress-fill {
    height: 100%;
    background: linear-gradient(135deg, #56ab2f, #a8e6cf);
    transition: width 0.3s ease;
    border-radius: 10px;
}

