* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Arial', sans-serif;
    line-height: 1.6;
    color: #333;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    padding: 20px;
}

h1 {
    text-align: center;
    color: white;
    margin-bottom: 30px;
    font-size: 2rem;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

.demo-container {
    max-width: 800px;
    margin: 0 auto;
}

.demo-section {
    background: white;
    margin-bottom: 30px;
    padding: 25px;
    border-radius: 15px;
    box-shadow: 0 8px 32px rgba(0,0,0,0.1);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.demo-section:hover {
    transform: translateY(-5px);
    box-shadow: 0 12px 40px rgba(0,0,0,0.15);
}

.demo-section h2 {
    color: #4a5568;
    margin-bottom: 15px;
    font-size: 1.3rem;
    border-bottom: 2px solid #e2e8f0;
    padding-bottom: 8px;
}

#demo-text {
    background: #f7fafc;
    padding: 15px;
    border-radius: 8px;
    border: 2px solid #e2e8f0;
    margin-bottom: 15px;
    font-size: 1.1rem;
    min-height: 50px;
    transition: all 0.3s ease;
}

#html-content {
    background: #f7fafc;
    padding: 15px;
    border-radius: 8px;
    border: 2px solid #e2e8f0;
    margin-bottom: 15px;
    min-height: 100px;
    transition: all 0.3s ease;
}

.demo-img {
    width: 200px;
    height: 200px;
    object-fit: cover;
    border-radius: 12px;
    margin-bottom: 15px;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

.style-box {
    background: #4299e1;
    color: white;
    padding: 30px;
    text-align: center;
    border-radius: 12px;
    margin-bottom: 15px;
    font-size: 1.2rem;
    font-weight: bold;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

.button-group {
    display: flex;
    gap: 10px;
    flex-wrap: wrap;
    justify-content: flex-start;
}

.demo-button {
    background: linear-gradient(45deg, #667eea, #764ba2);
    color: white;
    border: none;
    padding: 12px 20px;
    border-radius: 25px;
    cursor: pointer;
    font-size: 0.9rem;
    font-weight: 500;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(102, 126, 234, 0.3);
}

.demo-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(102, 126, 234, 0.4);
    background: linear-gradient(45deg, #5a67d8, #6b46c1);
}

.demo-button:active {
    transform: translateY(-1px);
}

.demo-button.reset {
    background: linear-gradient(45deg, #f56565, #e53e3e);
    box-shadow: 0 4px 15px rgba(245, 101, 101, 0.3);
}

.demo-button.reset:hover {
    background: linear-gradient(45deg, #e53e3e, #c53030);
    box-shadow: 0 8px 25px rgba(245, 101, 101, 0.4);
}

/* ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³åŠ¹æœ */
.fade-in {
    animation: fadeIn 0.5s ease-in;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.pulse {
    animation: pulse 0.5s ease-in-out;
}

@keyframes pulse {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}

/* ãƒ¬ã‚¹ãƒãƒ³ã‚·ãƒ–ãƒ‡ã‚¶ã‚¤ãƒ³ */
@media (max-width: 768px) {
    .demo-container {
        padding: 0 10px;
    }

    .demo-section {
        padding: 20px;
        margin-bottom: 20px;
    }

    .button-group {
        justify-content: center;
    }

    .demo-button {
        padding: 10px 16px;
        font-size: 0.85rem;
    }

    .demo-img {
        width: 150px;
        height: 150px;
    }

    h1 {
        font-size: 1.5rem;
    }
}

