expectedCss:
/* フォント基礎 - 段階的難易度設計に基づく実装 */

/* 基本レイアウト */
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 20px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: #333;
}

.container {
    max-width: 800px;
    margin: 0 auto;
    background: white;
    padding: 30px;
    border-radius: 15px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

/* メインタイトル */
h1 {
    color: #4a5568;
    font-size: 32px;
    text-align: center;
    margin-bottom: 30px;
    border-bottom: 3px solid #667eea;
    padding-bottom: 15px;
}

/* セクション */
.section {
    margin-bottom: 40px;
}

h2 {
    color: #2d3748;
    font-size: 24px;
    margin-bottom: 20px;
    border-left: 4px solid #667eea;
    padding-left: 15px;
}

/* フォント比較セクション */
.font-comparison {
    display: flex;
    flex-direction: column;
    gap: 20px;
}

.font-sample {
    background: #f7fafc;
    padding: 20px;
    border-radius: 10px;
    border: 2px solid #e2e8f0;
}

.font-sample h3 {
    color: #4a5568;
    margin: 0 0 10px 0;
    font-size: 18px;
}

.font-sample p {
    margin: 5px 0;
    line-height: 1.6;
}

/* フォントファミリーの設定 */
.default-font {
    font-family: inherit;
}

.arial-font {
    font-family: Arial, sans-serif;
}

.times-font {
    font-family: "Times New Roman", serif;
}

/* サイズ比較セクション */
.size-comparison {
    background: #f7fafc;
    padding: 25px;
    border-radius: 10px;
    border: 2px solid #e2e8f0;
    text-align: center;
}

.size-comparison p {
    margin: 15px 0;
    font-family: Arial, sans-serif;
}

/* フォントサイズの設定 */
.large-text {
    font-size: 24px;
    color: #2d3748;
    font-weight: bold;
}

.normal-text {
    font-size: 16px;
    color: #4a5568;
}

.small-text {
    font-size: 12px;
    color: #718096;
}

/* 組み合わせ比較セクション */
.combination-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 15px;
}

.combo-item {
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
    padding: 20px;
    border-radius: 10px;
    text-align: center;
    font-weight: bold;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

/* レスポンシブデザイン */
@media (max-width: 600px) {
    .container {
        padding: 20px;
    }

    .combination-grid {
        grid-template-columns: 1fr;
    }

    h1 {
        font-size: 28px;
    }

    h2 {
        font-size: 20px;
    }
}

