css.expected:
/* レイアウト基礎 - displayとfloatの基本 */

/* 基本スタイル */
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 20px;
    background-color: #f8f9fa;
}

h1 {
    text-align: center;
    color: darkblue;
    border-bottom: 3px solid blue;
    padding-bottom: 15px;
    margin-bottom: 40px;
}

h2 {
    color: darkgreen;
    margin-top: 50px;
    margin-bottom: 20px;
    border-left: 4px solid green;
    padding-left: 15px;
}

h3 {
    margin-top: 0;
    color: #333;
}

/* 1. displayプロパティのデモ */
.demo-area {
    background-color: #e9ecef;
    padding: 20px;
    margin: 20px 0;
    border: 2px solid #ccc;
}

.block-item {
    display: block;
    background-color: lightblue;
    padding: 10px;
    margin: 5px 0;
    border: 1px solid blue;
}

.inline-item {
    display: inline;
    background-color: lightcoral;
    padding: 10px;
    margin: 5px;
    border: 1px solid red;
}

/* 2. inline-blockで横並び */
.inline-block-item {
    display: inline-block;
    width: 120px;
    height: 80px;
    background-color: lightgreen;
    padding: 15px;
    margin: 10px;
    border: 2px solid green;
    text-align: center;
    vertical-align: top;
}

/* 3. 2カラムレイアウト */
.container {
    max-width: 800px;
    margin: 0 auto;
    background-color: white;
    border: 1px solid #ddd;
    overflow: hidden;
}

.sidebar {
    width: 200px;
    float: left;
    background-color: #f1f3f4;
    padding: 20px;
    border-right: 1px solid #ddd;
}

.main-content {
    margin-left: 241px;
    padding: 20px;
}

.sidebar ul {
    list-style: none;
    padding: 0;
}

.sidebar li {
    padding: 8px 0;
    border-bottom: 1px solid #ddd;
}

/* 4. 3カラムレイアウト */
.three-column-container {
    max-width: 900px;
    margin: 0 auto;
    overflow: hidden;
}

.column {
    width: 30%;
    float: left;
    background-color: white;
    margin: 1.5%;
    padding: 20px;
    border: 1px solid #ddd;
    border-radius: 8px;
}

/* 5. clearfixユーティリティ */
.clearfix {
    clear: both;
    margin: 30px 0;
}

/* 6. カードレイアウト */
.card-container {
    max-width: 900px;
    margin: 0 auto;
    text-align: center;
}

.card {
    display: inline-block;
    width: 200px;
    background-color: white;
    margin: 15px;
    padding: 20px;
    border: 1px solid #ddd;
    border-radius: 10px;
    vertical-align: top;
    text-align: left;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

.card:hover {
    box-shadow: 0 4px 10px rgba(0,0,0,0.15);
}
