css.template:
/* 基本スタイル */
body {
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 20px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: #333;
}

h1 {
    text-align: center;
    color: white;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 30px;
    margin: 0 0 40px 0;
    border-radius: 20px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
    font-size: 28px;
}

h2 {
    color: white;
    margin: 50px 0 30px 0;
    padding: 15px 20px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    backdrop-filter: blur(5px);
    font-size: 22px;
}

/* カードコンテナ */
.card-container {
    /* ここにdisplay: flex、flex-wrap: wrap、gap: 25px、justify-content: center、margin-bottom: 40pxを追加してください */
}

.stats-container {
    justify-content: space-around;
}

/* 基本カードスタイル */
.card {
    /* ここにbackground: white、border-radius: 16px、box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1)、transition: all 0.3s ease、overflow: hidden、position: relativeを追加してください */
}

.card:hover {
    /* ここにtransform: translateY(-8px)、box-shadow: 0 12px 40px rgba(0, 0, 0, 0.15)を追加してください */
}

/* 1. 基本カード */
.basic-card {
    padding: 30px;
    max-width: 300px;
    text-align: center;
}

.basic-card h3 {
    margin: 0 0 15px 0;
    color: #2c3e50;
    font-size: 20px;
}

.basic-card p {
    margin: 0;
    color: #7f8c8d;
    line-height: 1.6;
}

/* 2. 画像付きカード */
.image-card {
    /* ここにmax-width: 280px、overflow: hiddenを追加してください */
}

.card-image {
    height: 180px;
    background: linear-gradient(45deg, #667eea, #764ba2);
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
}

.placeholder-image {
    font-size: 48px;
    color: white;
    text-shadow: 0 2px 10px rgba(0, 0, 0, 0.3);
}

.card-content {
    padding: 25px;
    position: relative;
}

.card-content h3 {
    margin: 0 0 10px 0;
    color: #2c3e50;
    font-size: 18px;
}

.card-content p {
    margin: 0 0 15px 0;
    color: #7f8c8d;
    line-height: 1.5;
}

.card-tag {
    display: inline-block;
    background: #667eea;
    color: white;
    padding: 4px 12px;
    border-radius: 20px;
    font-size: 12px;
    font-weight: bold;
}

/* 3. アクション付きカード */
.action-card {
    /* ここにmax-width: 300px、padding: 25px、display: flex、flex-direction: columnを追加してください */
}

.action-card h3 {
    margin: 0 0 10px 0;
    color: #2c3e50;
    font-size: 20px;
}

.action-card p {
    margin: 0 0 auto 0;
    color: #7f8c8d;
    line-height: 1.6;
    flex-grow: 1;
}

.card-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 20px;
    padding-top: 20px;
    border-top: 1px solid #ecf0f1;
}

.card-price {
    font-size: 20px;
    font-weight: bold;
    color: #e74c3c;
}

.card-button {
    padding: 10px 20px;
    border: none;
    border-radius: 8px;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.2s ease;
    font-size: 14px;
}

.card-button.primary {
    background: #667eea;
    color: white;
}

.card-button.primary:hover {
    background: #5a6fd8;
    transform: translateY(-1px);
}

.card-button.secondary {
    background: #ecf0f1;
    color: #2c3e50;
    border: 2px solid #bdc3c7;
}

.card-button.secondary:hover {
    background: #d5dbdb;
    border-color: #95a5a6;
}

/* 4. プロフィールカード */
.profile-card {
    max-width: 280px;
    padding: 30px;
    text-align: center;
}

.profile-avatar {
    width: 80px;
    height: 80px;
    background: linear-gradient(45deg, #667eea, #764ba2);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 40px;
    margin: 0 auto 20px auto;
    box-shadow: 0 4px 20px rgba(102, 126, 234, 0.3);
}

.profile-card h3 {
    margin: 0 0 5px 0;
    color: #2c3e50;
    font-size: 22px;
}

.profile-role {
    margin: 0 0 15px 0;
    color: #667eea;
    font-weight: bold;
    font-size: 14px;
}

.profile-bio {
    margin: 0 0 25px 0;
    color: #7f8c8d;
    line-height: 1.5;
    font-size: 14px;
}

.profile-stats {
    display: flex;
    justify-content: space-around;
    margin-bottom: 25px;
    padding: 20px 0;
    border-top: 1px solid #ecf0f1;
    border-bottom: 1px solid #ecf0f1;
}

.stat {
    text-align: center;
}

.stat-number {
    display: block;
    font-size: 20px;
    font-weight: bold;
    color: #2c3e50;
}

.stat-label {
    font-size: 12px;
    color: #95a5a6;
    text-transform: uppercase;
}

/* 5. 統計カード */
.stats-card {
    padding: 25px;
    min-width: 200px;
    display: flex;
    align-items: center;
    gap: 20px;
}

.stats-icon {
    width: 60px;
    height: 60px;
    background: linear-gradient(45deg, #667eea, #764ba2);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
    flex-shrink: 0;
}

.stats-content h3 {
    margin: 0 0 5px 0;
    color: #7f8c8d;
    font-size: 14px;
    font-weight: normal;
    text-transform: uppercase;
}

.stats-number {
    margin: 0 0 5px 0;
    font-size: 24px;
    font-weight: bold;
    color: #2c3e50;
}

.stats-change {
    margin: 0;
    font-size: 12px;
    font-weight: bold;
}

.stats-change.positive {
    color: #27ae60;
}

.stats-change.positive::before {
    content: '↗ ';
}

.stats-change.negative {
    color: #e74c3c;
}

.stats-change.negative::before {
    content: '↘ ';
}

