expectedCss:
/* 基本的なTransition - 段階的難易度設計に基づく実装 */

/* 基本レイアウト */
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 20px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    background: white;
    padding: 30px;
    border-radius: 15px;
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2);
}

/* メインタイトル */
h1 {
    color: #4a5568;
    font-size: 32px;
    text-align: center;
    margin-bottom: 20px;
    border-bottom: 3px solid #667eea;
    padding-bottom: 15px;
}

/* イントロセクション */
.intro {
    text-align: center;
    margin-bottom: 40px;
    padding: 20px;
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    color: white;
    border-radius: 10px;
    font-size: 18px;
}

/* セクション */
.section {
    margin-bottom: 50px;
}

h2 {
    color: #2d3748;
    font-size: 24px;
    margin-bottom: 25px;
    border-left: 4px solid #667eea;
    padding-left: 15px;
}

/* 共通デモスタイル */
.transition-demo {
    background: white;
    padding: 25px;
    border-radius: 12px;
    text-align: center;
    cursor: pointer;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    min-height: 100px;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.transition-demo h3 {
    margin: 0 0 10px 0;
    color: #2d3748;
    font-size: 18px;
}

.transition-demo p {
    margin: 0;
    color: #718096;
    font-size: 14px;
}

/* デモグリッド */
.demo-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 25px;
    margin-bottom: 30px;
}

/* 1. 色の変化 */
.color-change {
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
    transition: all 0.4s ease;
}

.color-change:hover {
    background: linear-gradient(135deg, #f093fb, #f5576c);
    transform: translateY(-5px);
}

.text-color-change {
    background: #f7fafc;
    color: #667eea;
    transition: color 0.4s ease, background-color 0.4s ease;
}

.text-color-change:hover {
    color: #f5576c;
    background: #fff5f5;
}

/* 2. サイズの変化 */
.size-change {
    background: linear-gradient(135deg, #4facfe, #00f2fe);
    color: white;
    transition: transform 0.3s ease;
}

.size-change:hover {
    transform: scale(1.1);
}

.size-bounce {
    background: linear-gradient(135deg, #43e97b, #38f9d7);
    color: white;
    transition: transform 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.size-bounce:hover {
    transform: scale(1.15) rotate(2deg);
}

