/* ホバー効果 - :hoverセレクタによる高度なインタラクション */

/* 基本スタイル */
body {
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 20px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: white;
}

h1 {
    text-align: center;
    color: white;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 30px;
    margin: 0 0 40px 0;
    border-radius: 20px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
    font-size: 28px;
}

h2 {
    color: white;
    margin: 50px 0 30px 0;
    padding: 15px 20px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    backdrop-filter: blur(5px);
    font-size: 22px;
}

/* デモセクション */
.demo-section {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    justify-content: center;
    margin-bottom: 40px;
}

.button-section {
    gap: 15px;
}

/* 1. 基本的なホバー効果 */
.hover-item {
    background: rgba(255, 255, 255, 0.9);
    color: #333;
    padding: 20px 30px;
    border-radius: 12px;
    cursor: pointer;
    font-weight: bold;
    text-align: center;
    min-width: 120px;
    user-select: none;
    /* ここにtransition: all 0.3s easeを追加してください */
}

/* 拡大効果 */
.scale-up:hover {
    /* ここにtransform: scale(1.1)とbox-shadow: 0 10px 25px rgba(0, 0, 0, 0.3)を追加してください */
}

/* 回転効果 */
.rotate:hover {
    /* ここにtransform: rotate(5deg)、background: linear-gradient(45deg, #ff6b6b, #4ecdc4)、color: whiteを追加してください */
}

/* スライド効果 */
.slide-right:hover {
    /* ここにtransform: translateX(10px)、background: #2ecc71、color: whiteを追加してください */
}

/* フェード効果 */
.fade-opacity:hover {
    opacity: 0.6;
    transform: scale(0.95);
}

/* 2. 色変化効果 */
.color-change:hover {
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
    transform: translateY(-3px);
}

.text-color:hover {
    color: #e74c3c;
    background: #fff;
    border: 2px solid #e74c3c;
}

.gradient-bg:hover {
    background: linear-gradient(45deg, #ff9a9e, #fecfef, #fecfef, #ff9a9e);
    background-size: 400% 400%;
    animation: gradientShift 1s ease infinite;
}

@keyframes gradientShift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
}

/* 3. 複合効果 */
.complex-1:hover {
    /* ここにtransform: scale(1.1) rotate(-2deg)、background: linear-gradient(45deg, #667eea, #764ba2)、color: whiteを追加してください */
    box-shadow: 0 15px 30px rgba(102, 126, 234, 0.4);
}

.complex-2:hover {
    transform: translateY(-8px) scale(1.05);
    background: #f39c12;
    color: white;
    box-shadow: 0 12px 24px rgba(243, 156, 18, 0.4);
}

.complex-3:hover {
    transform: perspective(1000px) rotateX(10deg);
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

/* 4. ボタンホバー効果 */
.btn {
    padding: 12px 25px;
    border: none;
    border-radius: 25px;
    font-size: 16px;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    min-width: 140px;
    position: relative;
    overflow: hidden;
}

.btn-primary {
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
}

.btn-primary:hover {
    background: linear-gradient(135deg, #5a6fd8, #6a4190);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(102, 126, 234, 0.4);
}

.btn-secondary {
    background: #95a5a6;
    color: white;
}

.btn-secondary:hover {
    background: #7f8c8d;
    transform: scale(1.05);
    box-shadow: 0 5px 15px rgba(149, 165, 166, 0.4);
}

.btn-outline {
    background: transparent;
    color: white;
    border: 2px solid white;
}

.btn-outline:hover {
    background: white;
    color: #667eea;
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(255, 255, 255, 0.3);
}

.btn-gradient {
    background: linear-gradient(45deg, #ff6b6b, #4ecdc4);
    color: white;
    position: relative;
}

.btn-gradient:hover {
    transform: scale(1.05);
    box-shadow: 0 10px 30px rgba(255, 107, 107, 0.4);
}

/* 5. カードホバー効果 */
.card-section {
    display: flex;
    flex-wrap: wrap;
    gap: 30px;
    justify-content: center;
    margin-top: 30px;
}

.card {
    background: white;
    color: #333;
    padding: 25px;
    border-radius: 15px;
    max-width: 250px;
    text-align: center;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    transition: all 0.4s ease;
    cursor: pointer;
}

.card h3 {
    margin: 0 0 15px 0;
    color: #2c3e50;
}

.card p {
    margin: 0;
    color: #7f8c8d;
    line-height: 1.6;
}

/* リフトカード */
.lift-card:hover {
    transform: translateY(-15px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
}

/* チルトカード */
.tilt-card:hover {
    transform: rotate(2deg) scale(1.05);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

/* グローカード */
.glow-card:hover {
    transform: translateY(-5px);
    box-shadow: 
        0 10px 30px rgba(102, 126, 234, 0.3),
        0 0 50px rgba(102, 126, 234, 0.2);
    border: 2px solid rgba(102, 126, 234, 0.3);
}

