    margin-bottom: 10px;
}

.step-info {
    text-align: center;
    margin-bottom: 30px;
    opacity: 0.9;
}

.counter-section {
    background: rgba(255, 255, 255, 0.15);
    padding: 40px;
    border-radius: 20px;
    margin: 25px 0;
    text-align: center;
    border: 2px solid rgba(255, 255, 255, 0.2);
}

.counter-display {
    font-size: 4rem;
    font-weight: bold;
    color: #FFE135;
    margin: 30px 0;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
    background: rgba(0, 0, 0, 0.2);
    padding: 20px;
    border-radius: 15px;
    border: 3px solid rgba(255, 255, 255, 0.3);
}

.counter-buttons {
    display: flex;
    justify-content: center;
    gap: 20px;
    margin: 30px 0;
    flex-wrap: wrap;
}

.counter-btn {
    background: linear-gradient(135deg, #ff6b6b 0%, #4ecdc4 100%);
    color: white;
    border: none;
    padding: 15px 30px;
    font-size: 1.5rem;
    border-radius: 50px;
    cursor: pointer;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
    transition: all 0.3s ease;
    font-weight: bold;
    min-width: 120px;
}

.counter-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);
}

.counter-btn:active {
    transform: translateY(0);
}

.counter-btn.plus {
    background: linear-gradient(135deg, #56ab2f 0%, #a8e6cf 100%);
}

.counter-btn.minus {
    background: linear-gradient(135deg, #ff416c 0%, #ff4b2b 100%);
}

.counter-btn.reset {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.info-box {
    background: rgba(255, 255, 255, 0.15);
    padding: 25px;
    border-radius: 15px;
    margin: 20px 0;
    border-left: 5px solid #FFE135;
}

.highlight {
    color: #FFE135;
    font-weight: bold;
}

.code-example {
    background: rgba(0, 0, 0, 0.3);
    padding: 15px;
    border-radius: 8px;
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    margin: 15px 0;
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.instruction {
    background: rgba(255, 193, 7, 0.2);
    padding: 20px;
    border-radius: 10px;
    border: 2px solid #FFE135;
    margin: 20px 0;
}

.celebration {
    font-size: 3rem;
    text-align: center;
    margin: 20px 0;
}

.stats-section {
    background: rgba(255, 255, 255, 0.1);
    padding: 20px;
    border-radius: 15px;
    margin: 20px 0;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 15px;
    margin: 15px 0;
}

.stat-item {
    background: rgba(0, 0, 0, 0.2);
    padding: 15px;
    border-radius: 10px;
    text-align: center;
}

.stat-number {
    font-size: 1.8rem;
    font-weight: bold;
    color: #FFE135;
}

.counter-status {
    padding: 10px;
    border-radius: 8px;
    margin: 10px 0;
    text-align: center;
    font-weight: bold;
}

.status-positive {
    background: linear-gradient(135deg, #56ab2f, #a8e6cf);
}

.status-negative {
    background: linear-gradient(135deg, #ff416c, #ff4b2b);
}

.status-zero {
    background: linear-gradient(135deg, #667eea, #764ba2);
}

