body {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    max-width: 1000px;
    margin: 40px auto;
    padding: 20px;
    line-height: 1.6;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    min-height: 100vh;
}

.container {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    padding: 40px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

h1 {
    color: #FFE135;
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 10px;
}

.step-info {
    text-align: center;
    margin-bottom: 30px;
    opacity: 0.9;
}

.demo-section {
    background: rgba(255, 255, 255, 0.15);
    padding: 30px;
    border-radius: 20px;
    margin: 25px 0;
}

.text-display {
    background: rgba(255, 255, 255, 0.9);
    color: #2c3e50;
    padding: 25px;
    border-radius: 15px;
    margin: 20px 0;
    font-size: 1.3rem;
    font-weight: bold;
    text-align: center;
    min-height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 3px solid rgba(255, 255, 255, 0.3);
    transition: all 0.3s ease;
}

.button-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 15px;
    margin: 25px 0;
}

.text-btn {
    background: linear-gradient(135deg, #ff6b6b 0%, #4ecdc4 100%);
    color: white;
    border: none;
    padding: 15px 20px;
    font-size: 1rem;
    border-radius: 12px;
    cursor: pointer;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
    transition: all 0.3s ease;
    font-weight: bold;
}

.text-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);
}

.text-btn:active {
    transform: translateY(0);
}

.btn-greeting { background: linear-gradient(135deg, #56ab2f, #a8e6cf); }
.btn-time { background: linear-gradient(135deg, #667eea, #764ba2); }
.btn-motivation { background: linear-gradient(135deg, #fa709a, #fee140); }
.btn-learning { background: linear-gradient(135deg, #ff9a9e, #fecfef); }
.btn-progress { background: linear-gradient(135deg, #a8edea, #fed6e3); }
.btn-clear { background: linear-gradient(135deg, #6c757d, #495057); }

.info-box {
    background: rgba(255, 255, 255, 0.15);
    padding: 25px;
    border-radius: 15px;
    margin: 20px 0;
    border-left: 5px solid #FFE135;
}

.highlight {
    color: #FFE135;
    font-weight: bold;
}

.code-example {
    background: rgba(0, 0, 0, 0.3);
    padding: 15px;
    border-radius: 8px;
    font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
    margin: 15px 0;
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.instruction {
    background: rgba(255, 193, 7, 0.2);
    padding: 20px;
    border-radius: 10px;
    border: 2px solid #FFE135;
    margin: 20px 0;
}

.celebration {
    font-size: 3rem;
    text-align: center;
    margin: 20px 0;
}

.stats-section {
    background: rgba(0, 0, 0, 0.2);
    padding: 20px;
    border-radius: 15px;
    margin: 20px 0;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 15px;
    margin: 15px 0;
}

.stat-item {
    background: rgba(255, 255, 255, 0.1);
    padding: 15px;
    border-radius: 10px;
    text-align: center;
}

.stat-number {
    font-size: 1.8rem;
    font-weight: bold;
    color: #FFE135;
}

.current-text {
    background: rgba(255, 255, 255, 0.1);
    padding: 15px;
    border-radius: 10px;
    margin: 15px 0;
    font-style: italic;
}

.feature-list {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 15px;
    margin: 20px 0;
}

.feature-card {
    background: rgba(0, 0, 0, 0.2);
    padding: 20px;
    border-radius: 15px;
    text-align: center;
}

.feature-icon {
    font-size: 2rem;
    margin-bottom: 10px;
}

