// 問題3: オブジェクトのundefined処理（プロパティ存在確認）
// オブジェクトにないプロパティへのアクセスとundefined処理

let book = {
  title: "JavaScript入門",
  author: "プログラミング太郎",
  price: 2800
};

// 既存プロパティの表示
/* ここに「書籍名: 」+ タイトルを表示するconsole.logを書く */;
console.log("著者: " + book.author);

// 存在しないプロパティにアクセス（undefinedになる）
let publisher = book.publisher; // publisherプロパティは存在しない
/* ここに「出版社: 」+ publisher変数を表示するconsole.logを書く */;

// undefinedの確認と条件分岐
if (/* ここにpublisherがundefinedかどうかの条件を書く */) {
  console.log("出版社情報は登録されていません。");
} else {
  console.log("出版社: " + publisher);
}

// ページ数プロパティをチェック
let pages = book.pages;
if (pages === undefined) {
  /* ここにページ数が未登録の場合のメッセージ出力を書く */;
} else {
  console.log("ページ数: " + pages + "ページ");
}
