visual.content:
// 求人データからマッチング条件で絞り込み
const jobs = [
  {title: 'フロントエンドエンジニア', location: '東京', salary: 6000000, remote: true},
  {title: 'バックエンドエンジニア', location: '大阪', salary: 5500000, remote: true},
  {title: 'データサイエンティスト', location: '東京', salary: 7000000, remote: false},
  {title: 'デザイナー', location: '東京', salary: 4500000, remote: true}
];
// 東京 × 年収500万以上 × リモート可の求人

explanation.example:
const users = [
  {age: 25, city: '東京', active: true},
  {age: 30, city: '大阪', active: true}
];
const result = users.filter(u => u.age >= 25 && u.city === '東京' && u.active);
// 25歳以上かつ東京在住かつアクティブなユーザー
