explanation.example:
const students = [
  { name: 'A', score: 85 },
  // 合格
  { name: 'B', score: 45 },
  // 不合格
  { name: 'C', score: 92 },
  // 合格
  { name: 'D', score: 38 }
  // 不合格
];
let count = 0, total = 0;
for (let i = 0; i < students.length; i++) {
  if (students[i].score >= 60) {
    count++;
    // 合格者数: 2人
    total += students[i].score;
    // 合格者の合計: 85 + 92 = 177
  }
}
console.log('合格者: ' + count + '人, 平均: ' + (total / count) + '点');

// 合格者: 2人, 平均: 88.5点
