question.text:
次のコードでconsole.logの出力は何ですか？
let message = 'グローバル';
function test() {
  console.log(message);
}
test();

visual.content:
let message = 'グローバル';
function test() {
  console.log(message);
}
test();

// 出力は？

explanation.example:
let message = 'グローバル';
function test() {
  console.log(message);

// 'グローバル'
}
test();

// ローカル変数が優先される例
function test2() {
  let message = 'ローカル';
  console.log(message);

// 'ローカル'
}
