explanation.example:
// 関数式（無名関数）
const greet = function() {
  console.log('こんにちは');
};

// 名前付き関数式
const greet2 = function sayHello() {
  console.log('こんにちは');
};

// アロー関数式
const greet3 = () => {
  console.log('こんにちは');
};

greet(); // 呼び出し
