// nameが渡されない場合は'ゲスト'を使用
function greet(_) {
  console.log('こんにちは、' + name);
}

explanation.example:
function greet(name = 'ゲスト') {
  console.log('こんにちは、' + name);
}

greet('太郎');
// 'こんにちは、太郎'
greet();
// 'こんにちは、ゲスト'
greet(undefined);
// 'こんにちは、ゲスト'
greet(null);
// 'こんにちは、null'

// 複数のデフォルト引数
function introduce(name = '名無し', age = 0) {
  console.log(`${name}さん、${age}歳`);
}
