question.text:
次のコードでmyFuncには何が代入されますか？
function sayHi() {
  return 'Hi!';
}
let myFunc = sayHi;

visual.content:
function sayHi() {
  return 'Hi!';
}
let myFunc = sayHi;
// ()なし
// myFuncには何が入る？

explanation.example:
function sayHi() {
  return 'Hi!';
}

// 関数自体を代入
let myFunc = sayHi;
// 関数オブジェクト
console.log(typeof myFunc);
// 'function'
console.log(myFunc());
// 'Hi!' (関数を実行)

// 関数の実行結果を代入
let result = sayHi();
// 'Hi!' (文字列)
console.log(typeof result);
// 'string'

// 配列に関数を格納
let funcs = [sayHi, myFunc];
// 関数の配列
